(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["chunk-2d20fa6e"],{b516:function(e){e.exports={112101:{name:"Ciri",flavor:"Posso andare dove voglio, quando voglio.",info:"Ogni volta che perdi un round, riprendi in mano questa unità dal campo di battaglia.\n",infoRaw:"Ogni volta che perdi un round, riprendi in mano questa unità dal campo di battaglia.\n"},112102:{name:"Geralt: Igni",flavor:"Con uno schiocco di dita un witcher può accendere una lampada... o incenerire un nemico.",info:"Schieramento, Prima fila: distrugge tutte le unità più forti di una fila nemica con forza totale di almeno 20.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge tutte le unità più forti di una fila nemica con forza totale di almeno 20.\n"},112103:{name:"Geralt di Rivia",flavor:"Se è ciò che serve per salvare il mondo, allora è meglio che il mondo finisca.",info:"Schieramento: distrugge un nemico con almeno 8 di forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un nemico con almeno 8 di forza.\n"},112104:{name:"Regis",flavor:"Gli uomini... quelli più educati... mi chiamano mostro. O succhiasangue.",info:"Schieramento: danneggia di 1 tutti i nemici di una fila. Se almeno uno di essi viene distrutto, ripeti l'abilità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 tutti i nemici di una fila. Se almeno uno di essi viene distrutto, ripeti l'abilità.\n"},112105:{name:"Regis: vampiro antico",flavor:"Può rendersi invisibile. Il suo sguardo induce sonni profondi. Beve fino a saziarsi, poi si trasforma in pipistrello e vola via. Semplice, no?",info:"Schieramento, Prima fila: prosciuga tutti i potenziamenti di un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=drain>prosciuga</keyword> tutti i potenziamenti di un nemico.\n"},112106:{name:"Triss Merigold",flavor:"Posso farcela da sola. Fidati.",info:"Schieramento, Retrovie: svela un'unità casuale del tuo mazzo e danneggia un nemico di un valore pari alla relativa forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=reveal>svela</keyword> un'unità casuale del tuo mazzo e danneggia un nemico di un valore pari alla relativa forza.\n"},112107:{name:"Villentretenmerth",flavor:"Si fa chiamare Borkh Tre-Fringuelli. Non è bravo con i nomi.",info:"Dopo 2 turni, alla fine del turno, distrugge l'unità più forte in campo escludendo questa carta.\n",infoRaw:"Dopo 2 turni, alla fine del turno, distrugge l'unità più forte in campo escludendo questa carta.\n"},112108:{name:"Yennefer di Vengerberg",flavor:"La magia è caos, arte e scienza. È maledizione, benedizione e progresso.",info:"Schieramento, Prima fila: danneggia di 2 tutte le altre unità. \nSchieramento, Retrovie: potenzia di 2 tutte le altre unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 tutte le altre unità. \n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 2 tutte le altre unità.\n"},112109:{name:"Zoltan: furfante",flavor:"Chiedo scusa. Il mio animaletto esotico è un uccello sveglio, ma privo di pudore. Ho pagato dieci talleri per questa bellezza.",info:"Schieramento, Prima fila: evoca e mette in gioco Duda: agitatore.\nSchieramento, Retrovie: evoca e mette in gioco Duda: compagno.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=spawn>evoca</keyword> e mette in gioco Duda: agitatore.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=spawn>evoca</keyword> e mette in gioco Duda: compagno.\n"},112110:{name:"Ciri: corsa",flavor:"Sai quando le fiabe cessano di essere tali? Quando la gente inizia a crederci.",info:"Dopo 5 turni, alla fine del turno, pesca una carta.\n",infoRaw:"Dopo 5 turni, alla fine del turno, pesca una carta.\n"},112111:{name:"Geralt: Aard",flavor:"Un'esplosione di energia concentrata che annienta tutto ciò che incontra. L'ideale, quando dimentichi le chiavi di casa.",info:"Schieramento, Prima fila: danneggia 3 nemici di 2 e, se possibile, li sposta sulla fila del combattimento a distanza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia 3 nemici di 2 e, se possibile, li sposta sulla fila del combattimento a distanza.\n"},112112:{name:"Avallac'h: saggio",flavor:"Tra gli elfi liberi c'erano alcuni Aen Saevherne, o Saggi. Erano un mistero che alimentava molte leggende.",info:"Schieramento, Retrovie: mette in gioco un manufatto dal tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: mette in gioco un manufatto dal tuo mazzo.\n"},112113:{name:"Yennefer: invocatrice",flavor:"Una brava maga sa quando evocare il ghiaccio e quando il fuoco.",info:"Ordine: danneggia di 1 l'unità o le unità più forti. \nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 l'unità o le unità più forti. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},112201:{name:"Dudu",flavor:"La sua specie ha avuto molti nomi in passato, ma oggi vengono chiamati mutaforma.",info:"Schieramento: si trasforma nella copia base di un'unità nemica.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si trasforma nella copia base di un'unità nemica.\n"},112202:{name:"Eskel",flavor:"Sono un semplice witcher, Lupo. Non affronto draghi, non faccio amicizia con i re e non mi intrattengo con le maghe...",info:"Schieramento: richiama Vesemir e Lambert dal tuo mazzo su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=summon>richiama</keyword> Vesemir e Lambert dal tuo mazzo su questa fila.\n"},112203:{name:"Vesemir",flavor:"Se stanno per impiccarti, chiedi da bere. Può succedere di tutto nel frattempo.",info:"Schieramento: richiama Eskel e Lambert dal tuo mazzo su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=summon>richiama</keyword> Eskel e Lambert dal tuo mazzo su questa fila.\n"},112204:{name:"Lambert",flavor:"Questo è il tipo di trattativa che preferisco.",info:"Schieramento: richiama Eskel e Vesemir dal tuo mazzo su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=summon>richiama</keyword> Eskel e Vesemir dal tuo mazzo su questa fila.\n"},112205:{name:"Myrgtabrakke",flavor:"Mai frapporsi tra una draghessa e il suo cucciolo.",info:"Ordine: danneggia di 2 un nemico. \nCariche: 2.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 2 un nemico. \n<keyword=charge>Cariche</keyword>: 2.\n"},112206:{name:"Ocvist",flavor:"Signore del Monte Quarzo, Distruttore, Massacratore di Trajan. Nel tempo libero ama le lunghe passeggiate e le cene a lume di candela.",info:"Ordine: danneggia di 1 tutti i nemici.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 tutti i nemici.\n"},112207:{name:"Olgierd von Everec",flavor:"Almeno ora sai che non perdo facilmente la testa.",info:"Ordine: si cura.\nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: si <keyword=heal>cura</keyword>.\n<keyword=cooldown>Recupero</keyword>: 1.\n"},112208:{name:"Operatore",flavor:"Mentre tempo e spazio collassano davanti a noi, si espandono alle nostre spalle. In questo senso avanziamo in entrambi.",info:"Schieramento: evoca e richiama una copia base di un'unità Bronzo della tua mano su questa fila in entrambe le metà di campo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> e <keyword=summon>richiama</keyword> una copia base di un'unità Bronzo della tua mano su questa fila in entrambe le metà di campo.\n"},112209:{name:"Mucca premiata",flavor:"Muuu.",info:"Ordine: si trasforma in un Chort.\n",infoRaw:"<keyword=order>Ordine</keyword>: si trasforma in un Chort.\n"},112210:{name:"Rutilia",flavor:"Geralt, sarò onesta: non offenderti, eh, ma a cavalcare non ci sai proprio fare.",info:"Ogni volta che giochi una carta Oro, richiama questa unità dal tuo mazzo su una fila alleata casuale.\n",infoRaw:"Ogni volta che giochi una carta Oro, <keyword=summon>richiama</keyword> questa unità dal tuo mazzo su una fila alleata casuale.\n"},112211:{name:"Johnny",flavor:"Cosa sarebbe la vita senza poter dire tutti e trentatré trotterellando?",info:"Ordine: danneggia di 2 un'unità. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che giochi una carta Speciale.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 2 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che giochi una carta Speciale.\n"},112212:{name:"Sarah",flavor:"La piccola Sarah vuole giocare!",info:"Ordine: potenzia di 2 un'unità. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che giochi una carta Speciale.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 2 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che giochi una carta Speciale.\n"},112213:{name:"Re dei Mendicanti",flavor:"Potrei scoprire che sto benissimo senza le orecchie. O le mani. Pare che il Re dei Mendicanti accetti entrambe le cose come pagamento parziale.",info:"Alla fine di ogni turno, potenzia di 1 l'unità meno forte nella tua mano.\n",infoRaw:"Alla fine di ogni turno, potenzia di 1 l'unità meno forte nella tua mano.\n"},112214:{name:"Cyprian Wiley",flavor:"È uno dei capi che controllano i bassifondi della città, insieme a Sigi Reuven, a Carlo Mannaia Varese e al Re dei Mendicanti.",info:"Schieramento: bandisce un'unità con al massimo 3 di forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=banish>bandisce</keyword> un'unità con al massimo 3 di forza.\n"},112215:{name:"Iris von Everec",flavor:"Ricordo così poco... Ma quando penso alla mia rosa i ricordi iniziano a riaffiorare.",info:"Schieramento, Prima fila: distrugge un manufatto nemico.\nSchieramento, Retrovie: rimuove tutti gli effetti su fila dalla tua metà di campo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un manufatto nemico.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: rimuove tutti gli effetti su fila dalla tua metà di campo.\n"},112401:{name:"Unicorno",flavor:"Dicono che gli unicorni siano attratti dalle vergini. Poiché al giorno d'oggi le vergini sono più rare degli unicorni, risulta impossibile verificare questa teoria.",info:"Schieramento: potenzia di 4 un'unità. Se hai in mano il Chironex, raddoppia il potenziamento.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 4 un'unità. Se hai in mano il Chironex, raddoppia il potenziamento.\n"},112402:{name:"Chironex",flavor:"Per gli dei, non è un unicorno! È...!: le ultime parole di Vilemar, grande collezionista di curiosità.",info:"Schieramento: danneggia di 4 un'unità. Se hai in mano l'Unicorno, raddoppia i danni.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un'unità. Se hai in mano l'Unicorno, raddoppia i danni.\n"},112403:{name:"Duda: compagno",flavor:"Conosce un centinaio di parole: ottanta sono volgari, e il resto sono congiunzioni.",info:"Potenzia di 2 tutte le unità di una fila.\n",infoRaw:"Potenzia di 2 tutte le unità di una fila.\n"},112404:{name:"Duda: agitatore",flavor:"Il pappagallo di Zoltan aveva la straordinaria capacità di irritare chiunque. Zoltan compreso.",info:"Danneggia di 2 tutte le unità di una fila.\n",infoRaw:"Danneggia di 2 tutte le unità di una fila.\n"},112405:{name:"Demonio",flavor:"Il demonio somiglia ai cervi. A un enorme cervo malvagio.",info:"Schieramento: potenzia di 1 tutte le unità con Ultimo desiderio nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 tutte le unità con <keyword=deathwish>Ultimo desiderio</keyword> nella tua mano.\n"},113101:{name:"Ragh nar Roog",flavor:"Nel Tempo della Fine, Hemdall affronterà il male proveniente dalla terra di Mörhogg: legioni di wraith, demoni e spettri del Caos.",info:"Applica questo effetto a una fila nemica:\nAll'inizio di ogni turno, danneggia di 2 l'unità più forte di questa fila.\n",infoRaw:"Applica questo effetto a una fila nemica:\nAll'inizio di ogni turno, danneggia di 2 l'unità più forte di questa fila.\n"},113102:{name:"L'ultimo desiderio",flavor:"Un djinn, signori miei, esaudisce tre desideri. Fatto questo, fugge in una dimensione sconosciuta.",info:"Guarda le prime 2 carte del tuo mazzo, poi giocane una e bandisci l'altra.\n",infoRaw:"Guarda le prime 2 carte del tuo mazzo, poi giocane una e <keyword=banish>bandisci</keyword> l'altra.\n"},113201:{name:"Esca",flavor:"Quando resti a corto di contadini, le esche sono un ottimo surrogato.",info:"Rimescola un alleato dal campo di battaglia nel tuo mazzo, poi metti in gioco la prima unità del tuo mazzo.\n",infoRaw:"Rimescola un alleato dal campo di battaglia nel tuo mazzo, poi metti in gioco la prima unità del tuo mazzo.\n"},113202:{name:"Grandine di Merigold",flavor:"Il cielo si fece buio di colpo e le nubi si ammassarono sopra la città. Calò un'oscurità innaturale, accompagnata dal gelo. Accidenti! esclamò Yennefer. Sta per scatenarsi un disastro, temo.",info:"Danneggia 5 nemici di 2.\n",infoRaw:"Danneggia 5 nemici di 2.\n"},113203:{name:"Tempesta di Skellige",flavor:"Non è una normale tempesta. Questa è l'ira degli dei.",info:"Applica questo effetto a una fila nemica:\nPer 2 turni, all'inizio del turno, danneggia di 1 tutte le unità di questa fila.\n",infoRaw:"Applica questo effetto a una fila nemica:\nPer 2 turni, all'inizio del turno, danneggia di 1 tutte le unità di questa fila.\n"},113204:{name:"Sisma di Stammelford",flavor:"Il mago Stammelford spostò una montagna che ostruiva la vista dalla sua torre. Si dice che ci riuscì solo perché controllava un d'ao, elementale della terra.",info:"Danneggia di 1 tutti i nemici.\n",infoRaw:"Danneggia di 1 tutti i nemici.\n"},113205:{name:"Bomba al dimeritium",flavor:"Non può mancare nel bagaglio del cacciatore di streghe: in un lampo trasforma il mago più temibile in un agnellino pronto da affettare.",info:"Distrugge un manufatto.\n",infoRaw:"Distrugge un manufatto.\n"},113206:{name:"Bianco Gelo",flavor:"Ammirate Tedd Deireadh, il Tempo della Fine! Il mondo distrutto dal Bianco Gelo!",info:"Distrugge tutti i manufatti.\n",infoRaw:"Distrugge tutti i manufatti.\n"},113207:{name:"Corno del comandante",flavor:"Più uno al morale, meno uno all'udito.",info:"Potenzia 5 unità adiacenti di 3.\n",infoRaw:"Potenzia 5 unità adiacenti di 3.\n"},113208:{name:"Vaedermakar",flavor:"Il druido Vaedermakar controlla gli elementi. Può tacitare le tempeste, scatenare la grandine o evocare fulmini in grado di ridurre i nemici in cenere. Perciò dammi retta, trattalo con grande rispetto.",info:"Schieramento, Prima fila: aumenta di 1 la durata di tutti gli effetti su fila.\nSchieramento, Retrovie: riduce di 1 la durata di tutti gli effetti su fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: aumenta di 1 la durata di tutti gli effetti su fila.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: riduce di 1 la durata di tutti gli effetti su fila.\n"},113209:{name:"Evocazione di Alzur",flavor:"Alcune delle creature mostruose generate dal mago Alzur si aggirano per il mondo ancora oggi. Tra esse, il terribile Viy, che massacrò il suo stesso creatore e distrusse mezza Maribor prima di fuggire nei boschi di Riverdell.",info:"Metti in gioco l'unità più forte del tuo mazzo.\n",infoRaw:"Metti in gioco l'unità più forte del tuo mazzo.\n"},113301:{name:"Tuono di Alzur",flavor:"Non abbiamo alcuna possibilità di lanciare un incantesimo complesso come il Tuono di Alzur. Si dice che Alzur avesse una voce tonante come un corno, ma con la dizione di un fine oratore.",info:"Danneggia di 5 un'unità.\n",infoRaw:"Danneggia di 5 un'unità.\n"},113302:{name:"Gelo pungente",flavor:"Il bello del gelo è che i corpi non imputridiscono subito.",info:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia di 2 l'unità meno forte di questa fila.\n",infoRaw:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia di 2 l'unità meno forte di questa fila.\n"},113303:{name:"Cieli tersi",flavor:"Il sole splende, Dromle! Il sole splende! Forse dopo tutto c'è ancora speranza...",info:"Rimuove tutti gli effetti su fila dalla tua metà di campo.\n",infoRaw:"Rimuove tutti gli effetti su fila dalla tua metà di campo.\n"},113305:{name:"Nebbia impenetrabile",flavor:"Il sogno del buon comandante... e l'incubo di quello incapace.",info:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia di 2 l'unità più forte di questa fila.\n",infoRaw:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia di 2 l'unità più forte di questa fila.\n"},113306:{name:"Veleno di manticora",flavor:"Ti uccide prima ancora che tu possa recitare il nome completo dell'Imperatore di Nilfgaard.",info:"Danneggia 3 nemici di 3.\n",infoRaw:"Danneggia 3 nemici di 3.\n"},113307:{name:"Scarica d'adrenalina",flavor:"La bestia si scagliò verso di loro, con la furia negli occhi, immune al dolore e ai colpi sferrati da chi le si parava davanti. Nulla poteva fermarla...",info:"Fornisce o toglie Resistenza a un'unità.\n",infoRaw:"Fornisce o toglie <keyword=resilient>Resistenza</keyword> a un'unità.\n"},113308:{name:"Epidemia",flavor:"Le epidemie non rispettano né persone né confini.",info:"Distrugge tutte le unità meno forti.\n",infoRaw:"Distrugge tutte le unità meno forti.\n"},113309:{name:"Ustione",flavor:"Geralt arretrò di un passo. Aveva già visto uomini colpiti dall'Ustione. O, per la precisione, aveva visto ciò che restava di loro.",info:"Distrugge tutte le unità più forti.\n",infoRaw:"Distrugge tutte le unità più forti.\n"},113310:{name:"Rondine",flavor:"La rondine, simbolo della primavera e della rinascita, presta il suo nome a questa pozione che accelera il rimarginarsi delle ferite e il recupero della salute.",info:"Potenzia di 6 un'unità.\n",infoRaw:"Potenzia di 6 un'unità.\n"},113311:{name:"Tuono",flavor:"Il volto del witcher cambiò. I suoi tratti non erano più umani!",info:"Fervore.\nOrdine: potenzia di 3 un alleato. \nCariche: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: potenzia di 3 un alleato. \n<keyword=charge>Cariche</keyword>: 2.\n"},113312:{name:"Pioggia torrenziale",flavor:"Da queste parti persino la pioggia puzza.",info:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia 2 unità casuali della fila di 1.\n",infoRaw:"Applica questo effetto a una fila nemica:\nPer 4 turni, all'inizio del turno, danneggia 2 unità casuali della fila di 1.\n"},113313:{name:"An Craite: urlatore",flavor:"Ogni uomo, donna e bambino di Skellige può tagliare in due un nemico con l'ascia. Tuttavia, soltanto alcuni veterani sanno sfondare i timpani dei nemici con un urlo furioso.",info:"Schieramento: si potenzia di una quantità pari ai danni subiti da un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di una quantità pari ai danni subiti da un nemico.\n"},113315:{name:"Specchio di Bekker",flavor:"Scruta abbastanza a lungo nell'abisso, e sarà l'abisso a scrutare dentro di te.",info:"Scegli un'unità. Se è potenziata, la danneggia di due volte il valore del potenziamento. Se è danneggiata, la potenzia di due volte il valore del danneggiamento.\n",infoRaw:"Scegli un'unità. Se è potenziata, la danneggia di due volte il valore del potenziamento. Se è danneggiata, la potenzia di due volte il valore del danneggiamento.\n"},113316:{name:"Rinnovamento",flavor:"Medicus curat, magia sanat.",info:"Metti in gioco un'unità dal tuo cimitero.\n",infoRaw:"Metti in gioco un'unità dal tuo cimitero.\n"},113319:{name:"Catene di dimeritium",flavor:"Il mago aveva le braccia incatenate dietro la schiena. I Redaniani scossero le catene e Terranova urlò, agitandosi avanti e indietro, in preda a gemiti e conati di vomito. Era chiaro di cosa fossero fatte le sue manette.",info:"Blocca un'unità e la danneggia di 3.\n",infoRaw:"<keyword=lock>Blocca</keyword> un'unità e la danneggia di 3.\n"},113320:{name:"Mardroeme",flavor:"Se ne mangi abbastanza, il mondo non ti sembrerà più lo stesso...",info:"Danneggia di 3 un'unità, poi la potenzia di 9.\n",infoRaw:"Danneggia di 3 un'unità, poi la potenzia di 9.\n"},122101:{name:"Barone Sanguinario",flavor:"Non sono stato un buon padre, lo so, ma... forse non è troppo tardi!",info:"Ordine: raddoppia il numero di Cariche di un alleato.\n",infoRaw:"<keyword=order>Ordine</keyword>: raddoppia il numero di <keyword=charge>Cariche</keyword> di un alleato.\n"},122102:{name:"Vernon Roche",flavor:"Grande patriota, ma anche gran piantagrane.",info:"Spia.\nSchieramento: gioca le prime 2 carte del tuo mazzo.\n",infoRaw:"<keyword=spying>Spia</keyword>.\n<keyword=deploy>Schieramento</keyword>: gioca le prime 2 carte del tuo mazzo.\n"},122103:{name:"Jan Natalis",flavor:"Quella piazza dovrebbe essere intitolata agli uomini che sono morti, non a me.",info:"Prima fila: mentre questa unità è in campo, tutte le unità alleate con Ordine ottengono Fervore.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: mentre questa unità è in campo, tutte le unità alleate con <keyword=order>Ordine</keyword> ottengono <keyword=zeal>Fervore</keyword>.\n"},122107:{name:"Triss: farfalle",flavor:"Capitano! Le nostre frecce hanno... le ali!",info:"Schieramento: potenzia di 4 un'unità casuale nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 4 un'unità casuale nella tua mano.\n"},122108:{name:"Keira Metz",flavor:"Se devo morire oggi, voglio essere splendida per l'occasione.",info:"Schieramento, Retrovie: gioca un Incantesimo del tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: gioca un Incantesimo del tuo mazzo.\n"},122201:{name:"Dandelion",flavor:"Dandelion, sei un cinico bellimbusto, ma sei il mio migliore amico.",info:"Retrovie: ogni volta che un alleato ottiene una Carica, lo potenzia di 1.\n",infoRaw:"<keyword=ranged>Retrovie</keyword>: ogni volta che un alleato ottiene una <keyword=charge>Carica</keyword>, lo potenzia di 1.\n"},122202:{name:"Priscilla",flavor:"Immagina Dandelion vestito da donna e potrai farti un'idea.",info:"Fervore.\nOrdine: aggiunge 1 alle Cariche di un'unità. \nRecupero: 1.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: aggiunge 1 alle <keyword=charge>Cariche</keyword> di un'unità. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},122203:{name:"Thaler",flavor:"Al diavolo! Non siamo tutti dei donnaioli. Alcuni di noi credono nei sentimenti...",info:"Schieramento: rimuove tutte le Cariche da un nemico e le assegna a Thaler.\nOrdine: aggiunge 1 alle Cariche di un'unità alleata.\nCariche: 0.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: rimuove tutte le <keyword=charge>Cariche</keyword> da un nemico e le assegna a Thaler.\n<keyword=order>Ordine</keyword>: aggiunge 1 alle <keyword=charge>Cariche</keyword> di un'unità alleata.\n<keyword=charge>Cariche</keyword>: 0.\n"},122204:{name:"Ves",flavor:"Meglio vivere un giorno da re che una vita da mendicante.",info:"Schieramento: danneggia di 4 un nemico. \nOrdine: fornisce Fervore a un alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico. \n<keyword=order>Ordine</keyword>: fornisce <keyword=zeal>Fervore</keyword> a un alleato.\n"},122205:{name:"Sheala",flavor:"La Loggia pecca di superbia. La nostra brama di potere ci rovinerà.",info:"Schieramento: danneggia di 2 un nemico.\nOrdine: danneggia di 2 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico.\n<keyword=order>Ordine</keyword>: danneggia di 2 un nemico.\n"},122206:{name:"Sabrina Glevissig",flavor:"La figlia della selvaggia natura del Kaedwen.",info:"Spia. \nUltimo desiderio: danneggia di 2 tutte le unità di questa fila.\n",infoRaw:"<keyword=spying>Spia</keyword>. \n<keyword=deathwish>Ultimo desiderio</keyword>: danneggia di 2 tutte le unità di questa fila.\n"},122207:{name:"Dethmold",flavor:"Una volta ho fatto vomitare le interiora a un prigioniero. Ah, bei tempi!",info:"Ordine, Retrovie: danneggia un nemico di un valore pari al potenziamento di Dethmold.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia un nemico di un valore pari al potenziamento di Dethmold.\n"},122208:{name:"Principe Stennis",flavor:"Indossa un'armatura d'oro. Per forza è uno sbruffone.",info:"Schieramento: potenzia di 2 un alleato e aggiunge 1 alle sue Cariche.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un alleato e aggiunge 1 alle sue <keyword=charge>Cariche</keyword>.\n"},122210:{name:"Principessa Pavetta",flavor:"Si diceva che la regina fosse soggetta ad attacchi d'ira, ma non immaginavo nulla di simile...",info:"Schieramento: rimescola nel tuo mazzo un'unità Bronzo e tutte le sue copie presenti nel tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: rimescola nel tuo mazzo un'unità Bronzo e tutte le sue copie presenti nel tuo cimitero.\n"},122211:{name:"Margarita Laux-Antille",flavor:"Mi sta a cuore solo il bene di Aretuza e delle mie allieve.",info:"Fervore.\nOrdine: usa 3 delle sue Cariche per bloccare un'unità nemica.\nCariche: 3.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: usa 3 delle sue <keyword=charge>Cariche</keyword> per <keyword=lock>bloccare</keyword> un'unità nemica.\n<keyword=charge>Cariche</keyword>: 3.\n"},122212:{name:"Nenneke",flavor:"In pochi conoscono le arti curative come Nenneke.",info:"Ordine, Retrovie: potenzia di 1 un'unità. \nCariche: 3.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 3.\n"},122213:{name:"Odrin",flavor:"Bere senza Odrin è come remare senza remi.",info:"Schieramento: aggiunge 1 alle Cariche di tutte le unità alleate.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: aggiunge 1 alle <keyword=charge>Cariche</keyword> di tutte le unità alleate.\n"},122214:{name:"Kaedwen: sergente",flavor:"Avanti, uomini! Avanti, ho detto, o i nilfgaardiani saranno l'ultimo dei vostri problemi!",info:"Fervore.\nOrdine: potenzia di 1 un alleato. \nCariche: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: potenzia di 1 un alleato. \n<keyword=charge>Cariche</keyword>: 2.\n"},122216:{name:"Mannaia",flavor:"Chi vuole fare affari a Novigrad o si accorda con Mannaia, o può dire addio alle sue ginocchia.",info:"Schieramento: danneggia di 1 un'unità per ogni carta che hai in mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un'unità per ogni carta che hai in mano.\n"},122301:{name:"Balista",flavor:"L'arma che tutte le balestre sognano di diventare da grandi.",info:"Ordine: danneggia di 1 un'unità. \nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\n"},122302:{name:"Balista rinforzata",flavor:"Non colpisce mai due volte nello stesso punto. Il che, a pensarci bene, potrebbe essere un problema.",info:"Ordine: danneggia di 2 un'unità. \nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 2 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\n"},122303:{name:"Trabucco",flavor:"Non crederete che il castello crollerà da solo? Diamoci dentro con questi trabucchi!",info:"Ordine, Retrovie: danneggia di 1 un'unità. Se viene distrutta, danneggia di 1 le unità adiacenti.\nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia di 1 un'unità. Se viene distrutta, danneggia di 1 le unità adiacenti.\n<keyword=charge>Cariche</keyword>: 1.\n"},122304:{name:"Torre d'assedio",flavor:"L'ultima novità in materia di assedi alle città fortificate.",info:"Ordine, Prima fila: potenzia di 1 gli alleati adiacenti a questa unità. \nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: potenzia di 1 gli alleati adiacenti a questa unità. \n<keyword=charge>Cariche</keyword>: 1.\n"},122309:{name:"Aiuto-assediante",flavor:"Devi ricalibrare il braccio di cinque gradi. Devo fare cosa di quanto?",info:"Schieramento: aggiungi 2 alle Cariche di una Macchina da guerra alleata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: aggiungi 2 alle <keyword=charge>Cariche</keyword> di una Macchina da guerra alleata.\n"},122310:{name:"Bande Blu: ricognitore",flavor:"Bande Blu e Scoia'tael hanno una cosa in comune: sono spinti dall'odio.",info:"Schieramento, Prima fila: evoca in fondo al mazzo una copia di un alleato Bronzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=spawn>evoca</keyword> in fondo al mazzo una copia di un alleato Bronzo.\n"},122311:{name:"Bande Blu: commando",flavor:"Farei tutto per Temeria. In genere, uccido in suo nome.",info:"Ordine: richiama tutte le copie di questa unità dal tuo mazzo su questa fila.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=summon>richiama</keyword> tutte le copie di questa unità dal tuo mazzo su questa fila.\n"},122312:{name:"Medico da campo",flavor:"Cuci il rosso al rosso e il bianco al bianco, e andrà tutto bene.",info:"Schieramento: Potenzia le unità adiacenti di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: Potenzia le unità adiacenti di 2.\n"},122313:{name:"Stendardo Grigio",flavor:"State tutti calmi. E pronti. I veri proprietari di questi mantelli e cappelli in pelliccia di castoro potrebbero essere ovunque.",info:"Schieramento: ottiene Fervore se viene messa in gioco adiacente a 2 Soldati.\nOrdine: danneggia di 3 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: ottiene <keyword=zeal>Fervore</keyword> se viene messa in gioco adiacente a 2 Soldati.\n<keyword=order>Ordine</keyword>: danneggia di 3 un nemico.\n"},122314:{name:"Kaedwen: cavalleria",flavor:"Mi sono sempre chiesto una cosa: come diavolo fanno questi tizi quando la natura chiama...?",info:"Schieramento, Prima fila: rimuove tutte le Cariche da un nemico, poi lo danneggia di un valore pari alle Cariche rimosse.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: rimuove tutte le <keyword=charge>Cariche</keyword> da un nemico, poi lo danneggia di un valore pari alle Cariche rimosse.\n"},122315:{name:"Trabucco rinforzato",flavor:"Lo senti? La terra trema ogni volta che la Grande Bertha scaglia una pietra!",info:"Retrovie: alla fine di ogni turno, danneggia di 1 un nemico casuale sulla fila del combattimento a distanza.\n",infoRaw:"<keyword=ranged>Retrovie</keyword>: alla fine di ogni turno, danneggia di 1 un nemico casuale sulla fila del combattimento a distanza.\n"},122316:{name:"Temeria: fanteria",flavor:"Temeria! Temeria! Che gli dei ti ricoprano di gloria! E che i tuoi nemici siano maledetti, per l'eternità!",info:"Schieramento: danneggia di 1 un nemico per ogni unità potenziata presente su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico per ogni unità potenziata presente su questa fila.\n"},122318:{name:"Mastro d'assedio",flavor:"Non manco mai due volte.",info:"Ogni volta che una Macchina da guerra viene posizionata su questa fila, le fornisce Fervore.\n",infoRaw:"Ogni volta che una Macchina da guerra viene posizionata su questa fila, le fornisce <keyword=zeal>Fervore</keyword>.\n"},122401:{name:"Botchling",flavor:"Ammetti i tuoi errori e impara a seppellirli, o torneranno a perseguitarti.",info:"Alla fine di ogni turno, danneggia di 1 il nemico più forte. \nOrdine: si trasforma in un Lubberkin senza modificare la propria forza.\n",infoRaw:"Alla fine di ogni turno, danneggia di 1 il nemico più forte. \n<keyword=order>Ordine</keyword>: si trasforma in un Lubberkin senza modificare la propria forza.\n"},122402:{name:"Lubberkin",flavor:"Per te scelgo il nome di Dea e ti abbraccio come mia figlia.",info:"Alla fine di ogni turno, potenzia di 1 l'alleato meno forte. \nOrdine: si trasforma in un Botchling senza modificare la propria forza.\n",infoRaw:"Alla fine di ogni turno, potenzia di 1 l'alleato meno forte. \n<keyword=order>Ordine</keyword>: si trasforma in un Botchling senza modificare la propria forza.\n"},122403:{name:"Novizio di Vicovaro",flavor:"I maghi di Nilfgaard sono sacrificabili. Se qualcuno delude l'Imperatore, non mancheranno dei sostituti per soddisfare i suoi desideri.",info:"Schieramento, Retrovie: pesca una carta, poi sposta una carta della tua mano in cima al tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: pesca una carta, poi sposta una carta della tua mano in cima al tuo mazzo.\n"},123201:{name:"Rinforzi",flavor:"Suonate la ritirata! Raggruppiamoci in attesa di rinforzi!",info:"Evoca una copia di un'unità Bronzo alleata e la richiama su questa fila.\n",infoRaw:"<keyword=spawn>Evoca</keyword> una copia di un'unità Bronzo alleata e la <keyword=summon>richiama</keyword> su questa fila.\n"},131101:{name:"Eredin Bréacc Glas",flavor:"Abbi un po' di dignità! Sai come andrà a finire.",info:"Ordine: potenzia di 3 un'unità e le fornisce Immunità.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 3 un'unità e le fornisce <keyword=immune>Immunità</keyword>.\n"},131102:{name:"Ge'els",flavor:"I dipinti comunicano emozioni, non parole.",info:"Retrovie: ogni volta che metti in gioco un'unità con Ultimo desiderio, attiva la sua abilità Ultimo desiderio.\n",infoRaw:"<keyword=ranged>Retrovie</keyword>: ogni volta che metti in gioco un'unità con <keyword=deathwish>Ultimo desiderio</keyword>, attiva la sua abilità <keyword=deathwish>Ultimo desiderio</keyword>.\n"},132101:{name:"Draug",flavor:"Alcuni uomini non ammettono la sconfitta e continuano a combattere persino dalla tomba.",info:"Schieramento: trasforma tutti gli alleati umani in Kaedwen: revenant senza modificarne la forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: trasforma tutti gli alleati umani in Kaedwen: revenant senza modificarne la forza.\n"},132102:{name:"Imlerith: ira",flavor:"Si vantava che nessun'arma avrebbe mai perforato la sua corazza. E aveva ragione. Anche da morto.",info:"Danneggia un nemico di una quantità pari alla forza dell'alleato più forte.\n",infoRaw:"Danneggia un nemico di una quantità pari alla forza dell'alleato più forte.\n"},132103:{name:"Spirito della Foresta",flavor:"Non cacciamo mai in questi boschi, nemmeno a costo di morire di fame.",info:"Ordine: potenzia di 8 un'unità nella tua mano.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 8 un'unità nella tua mano.\n"},132104:{name:"Caranthir Ar-Feiniel",flavor:"Un figlio diletto che ha scelto la via del male.",info:"Schieramento: evoca una copia con forza 1 di un'unità della tua mano e la richiama su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> una copia con forza 1 di un'unità della tua mano e la <keyword=summon>richiama</keyword> su questa fila.\n"},132105:{name:"Avallac'h",flavor:"Voi umani avete... gusti insoliti.",info:"Ordine: fornisce Immunità a un'unità alleata.\n",infoRaw:"<keyword=order>Ordine</keyword>: fornisce <keyword=immune>Immunità</keyword> a un'unità alleata.\n"},132106:{name:"Custode",flavor:"Ci sono più cose in cielo e in terra di quante i filosofi possano mai sognare.",info:"Schieramento, Retrovie: metti in gioco un manufatto dal tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: metti in gioco un manufatto dal tuo cimitero.\n"},132107:{name:"Kayran",flavor:"Uccidere un kayran è facile: prendi la tua spada migliore, vendila e assolda un witcher.",info:"Fervore.\nOrdine: consuma un alleato.\nCariche: 3.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: <keyword=consume>consuma</keyword> un alleato.\n<keyword=charge>Cariche</keyword>: 3.\n"},132108:{name:"Miruna",flavor:"Perché combattere? Ci sono altri modi per sfogare la tensione in eccesso...",info:"Ultimo desiderio: sequestra un nemico con al massimo 3 di forza.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: <keyword=seize>sequestra</keyword> un nemico con al massimo 3 di forza.\n"},132201:{name:"Arachas gigante",flavor:"Un incrocio fra un granchio, un ragno e... una montagna.",info:"Schieramento: evoca 2 Arachas fuco e li richiama su questa fila, poi potenzia di 1 tutti gli altri Insettoidi sulla fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> 2 Arachas fuco e li <keyword=summon>richiama</keyword> su questa fila, poi potenzia di 1 tutti gli altri Insettoidi sulla fila.\n"},132202:{name:"Strega di malasorte",flavor:"Buona parte delle streghe dei sepolcri si nutre dei cadaveri decomposti profanati dalle tombe. Tuttavia, alcune si infiltrano nelle case, rapiscono i bambini, uccidono gli anziani e, in generale, si rivelano vicini indesiderabili.",info:"Ordine: bandisce tutte le unità del tuo cimitero, poi si potenzia di 1 per ognuna di esse.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=banish>bandisce</keyword> tutte le unità del tuo cimitero, poi si potenzia di 1 per ognuna di esse.\n"},132203:{name:"Abaya",flavor:"Ne ho viste di creature brutte in vita mia... murene, lamprede, mostri marini... ma mai niente di simile!",info:"Prosperità. \nSchieramento: attiva l'abilità Ultimo desiderio di un alleato Bronzo.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>. \n<keyword=deploy>Schieramento</keyword>: attiva l'abilità <keyword=deathwish>Ultimo desiderio</keyword> di un alleato Bronzo.\n"},132204:{name:"Terrorizzatore: dormiente",flavor:"Cosa ho fatto? urlò il mago, spaventato dalla sua creazione.",info:"Dopo la distruzione di 3 unità alleate, il Terrorizzatore si sveglia.\n",infoRaw:"Dopo la distruzione di 3 unità alleate, il Terrorizzatore si sveglia.\n"},132205:{name:"Morvudd",flavor:"Uno strano esemplare di demonio che infesta Ard Skellig. Insolitamente schizzinoso per quanto riguarda il cibo.",info:"Schieramento: potenzia di 1 tutte le Bestie alleate.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 tutte le Bestie alleate.\n"},132206:{name:"Whispess",flavor:"Sarei la donna migliore della tua vita... e l'ultima!",info:"Schieramento: danneggia di 2 un nemico.\nAumenta di 2 i danni di questa unità ogni volta che metti in gioco Brewess o Weavess.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico.\nAumenta di 2 i danni di questa unità ogni volta che metti in gioco Brewess o Weavess.\n"},132207:{name:"Brewess",flavor:"Ti faremo a pezzi, ragazzo. Faremo un brodo con la tua carne!",info:"Schieramento: consuma 1 unità alleata/e.\nAumenta di 1 il numero dei bersagli ogni volta che metti in gioco Whispess o Weavess.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=consume>consuma</keyword> 1 unità alleata/e.\nAumenta di 1 il numero dei bersagli ogni volta che metti in gioco Whispess o Weavess.\n"},132208:{name:"Weavess",flavor:"Sento il tuo dolore. Vedo la tua paura!",info:"Schieramento: potenzia di 2 un alleato.\nAumenta di 2 il potenziamento ogni volta che metti in gioco Whispess o Brewess.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un alleato.\nAumenta di 2 il potenziamento ogni volta che metti in gioco Whispess o Brewess.\n"},132209:{name:"Manticora imperiale",flavor:"Uno dei mostri più antichi e letali del mondo. Un tempo, affrontare queste creature era emozionante. Ora, queste bestie sono solo un ostacolo sul mio cammino. Le loro carni e il loro sangue mi aiuteranno a sopravvivere a questo inferno di ghiaccio.",info:"Ultimo desiderio: distrugge l'unità meno forte.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: distrugge l'unità meno forte.\n"},132210:{name:"Ifrit colossale",flavor:"Non sopporti il caldo? Allora non hai speranze.",info:"Fervore.\nOrdine: danneggia di 1 tutte le unità sulla fila opposta.\nRecupero: 3.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: danneggia di 1 tutte le unità sulla fila opposta.\n<keyword=cooldown>Recupero</keyword>: 3.\n"},132211:{name:"Nekker guerriero",flavor:"Prudenza, signori. Ci sono dei nekker sotto questo ponte.",info:"Prosperità.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>.\n"},132212:{name:"Gigante dei ghiacci",flavor:"In vita mia sono fuggito solo dal gigante dei ghiacci. E non me ne vergogno.",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},132213:{name:"D'ao primordiale",flavor:"Come si affronta un elementale della terra? Semplice: scappando a gambe levate.",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},132214:{name:"Nithral",flavor:"I guerrieri della Caccia Selvaggia sono i migliori sulla piazza, ma solo i cavalieri di Eredin sono i più brutali e feroci di Aen Elle.",info:"Schieramento: distrugge un manufatto nemico. Danneggia di 2 le unità adiacenti a esso se controlli l'unità più forte.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico. Danneggia di 2 le unità adiacenti a esso se controlli l'unità più forte.\n"},132215:{name:"Gaunter O'Dimm",flavor:"Può darti esattamente ciò che desideri. È questo il problema.",info:"Schieramento: fornisce l'attributo Condannata a un'unità nemica.\nOrdine: distrugge un nemico Condannato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: fornisce l'attributo <keyword=doomed>Condannata</keyword> a un'unità nemica.\n<keyword=order>Ordine</keyword>: distrugge un nemico <keyword=doomed>Condannato</keyword>.\n"},132216:{name:"Principe-rospo",flavor:"Grosso, cattivo, brutto. Si aggira per le fogne.",info:"Schieramento, Prima fila: consuma un'unità con al massimo 3 di forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=consume>consuma</keyword> un'unità con al massimo 3 di forza.\n"},132217:{name:"Arpia celaeno",flavor:"Le arpie comuni si nutrono di carogne. Le arpie celaeno si nutrono... di sogni.",info:"Schieramento: consuma un'unità alleata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=consume>consuma</keyword> un'unità alleata.\n"},132218:{name:"Punta-di-lancia: pace",flavor:"Non disturbare.",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},132301:{name:"Foglet",flavor:"La nebbia cala e i foglet strisciano sui corpi delle loro vittime.",info:"Ultimo desiderio: richiama una copia di questa unità dal tuo mazzo su una fila alleata casuale.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: <keyword=summon>richiama</keyword> una copia di questa unità dal tuo mazzo su una fila alleata casuale.\n"},132302:{name:"Foglet antico",flavor:"Molte paure ancestrali si annidano nel cuore dell'uomo. Tra queste, il timore della nebbia è fondato...",info:"Ultimo desiderio: richiama questa unità dal cimitero sulla stessa fila, la potenzia di 4 e le assegna l'attributo Condannata.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: <keyword=summon>richiama</keyword> questa unità dal cimitero sulla stessa fila, la potenzia di 4 e le assegna l'attributo <keyword=doomed>Condannata</keyword>.\n"},132303:{name:"Viverna",flavor:"Immagina l'incrocio fra un serpente alato e un incubo: la viverna è peggio.",info:"Schieramento: danneggia di 2 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico.\n"},132304:{name:"Arachas fuco",flavor:"L'aspetto ripugnante è il modo in cui la natura ci avverte di girare al largo.",info:"Condannata.\n",infoRaw:"<keyword=doomed>Condannata</keyword>.\n"},132305:{name:"Nekker",flavor:"Sarebbero quasi buffi, se non fosse per la loro feroce indole omicida.",info:"Prosperità.\nSchieramento: evoca una copia base di questa unità e la richiama su questa fila.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>.\n<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> una copia base di questa unità e la <keyword=summon>richiama</keyword> su questa fila.\n"},132306:{name:"Ghoul",flavor:"Se i ghoul sono parte del ciclo della vita, allora è un circolo vizioso.",info:"Schieramento, Prima fila: consuma un'unità del tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=consume>consuma</keyword> un'unità del tuo cimitero.\n"},132307:{name:"Grifone",flavor:"Ai grifoni piace giocare con le prede. Le mangiano vive, un pezzo alla volta.",info:"Schieramento: distrugge un'unità alleata su questa fila. Se non ci sono unità, si distrugge.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un'unità alleata su questa fila. Se non ci sono unità, si distrugge.\n"},132308:{name:"Guerriero Vran",flavor:"In sella ai loro cavalli, apparivano a loro agio. Chi ha visto le loro armi, racconta di lance corte a punta larga, spade con un'elsa singolare, asce da battaglia e ronconi dentellati.",info:"Ogni volta che un'unità viene distrutta durante il tuo turno, danneggia di 1 un nemico casuale.\n",infoRaw:"Ogni volta che un'unità viene distrutta durante il tuo turno, danneggia di 1 un nemico casuale.\n"},132309:{name:"Caccia: guerriero",flavor:"Il Bianco Gelo sta arrivando.",info:"Schieramento: danneggia un'unità di un valore pari al numero di unità della Caccia Selvaggia che controlli.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia un'unità di un valore pari al numero di unità della Caccia Selvaggia che controlli.\n"},132310:{name:"Caccia: cavaliere",flavor:"Prima saltano agli occhi le corna di bufalo sui loro elmi, seguite dal cimiero e infine dai loro volti scheletrici sotto la visiera.",info:"Schieramento: richiama una copia di Caccia: cavaliere dal tuo mazzo su questa fila se controlli l'unità più forte.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=summon>richiama</keyword> una copia di Caccia: cavaliere dal tuo mazzo su questa fila se controlli l'unità più forte.\n"},132312:{name:"Picchiata predatoria",flavor:"Temendo per le proprie vite, gli abitanti di Hengfors si ripararono all'ombra delle mura cittadine. E fecero solo bene...",info:"Entrambi i giocatori distruggono la propria unità meno forte.\n",infoRaw:"Entrambi i giocatori distruggono la propria unità meno forte.\n"},132314:{name:"Drowner",flavor:"Anche se il witcher chiede oro, per l'uccisione di un drowner non dargli più di un penny d'argento, o uno e mezzo al massimo.",info:"Prosperità.\nSchieramento: danneggia di 2 un nemico e lo sposta sull'altra sua fila.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>.\n<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico e lo sposta sull'altra sua fila.\n"},132315:{name:"Arpia",flavor:"Esistono molte specie di arpie, e sono tutte cleptomani.",info:"Condannata.\n",infoRaw:"<keyword=doomed>Condannata</keyword>.\n"},132316:{name:"Uovo di arpia",flavor:"L'omelette di uova di arpia è una squisitezza, signore! Ma assai cara, dal momento che quei mostri non si separano volentieri dalle loro uova.",info:"Ultimo desiderio: evoca un'Arpia e la richiama su questa fila.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: <keyword=spawn>evoca</keyword> un'Arpia e la <keyword=summon>richiama</keyword> su questa fila.\n"},132402:{name:"Caccia: segugio",flavor:"Gridate Sterminio! e sguinzagliate i mastini della guerra.",info:"Schieramento: se controlli l'unità più forte, danneggia un nemico di 3, altrimenti lo danneggia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se controlli l'unità più forte, danneggia un nemico di 3, altrimenti lo danneggia di 1.\n"},132403:{name:"Branco di lupi",flavor:"Tranquilli, ora vi mostro come si ammansiscono i lupi... - Ultime parole di Dunbar il Cacciatore.",info:"Schieramento: danneggia di 2 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico.\n"},132404:{name:"Ifrit infuriato",flavor:"C'era un mago che li trovava carini. Dalla sua sorte, il detto non si scherza con il fuoco.",info:"Schieramento: danneggia di 4 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico.\n"},132405:{name:"D'ao forsennato",flavor:"È facile scambiarli per rocce. Molti troll hanno avuto un'amara sorpresa proprio per questo motivo.",info:"Schieramento: distrugge un manufatto nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico.\n"},132406:{name:"Maerolorn",flavor:"Né porte né mura possono fermarli. Basta una piccola crepa... Non hanno bisogno d'altro.",info:"Schieramento: attiva l'abilità Ultimo desiderio di un alleato Bronzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: attiva l'abilità <keyword=deathwish>Ultimo desiderio</keyword> di un alleato Bronzo.\n"},132407:{name:"Golem imperiale",flavor:"I maghi più potenti di Nilfgaard hanno perfezionato l'arte della creazione dei golem. In alcune occasioni, sono perfino riusciti a schierarli per la causa imperiale...",info:"Schieramento: Svela la prima unità del mazzo avversario e si danneggia di un valore pari alla relativa forza, poi la rimescola nel mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=reveal>Svela</keyword> la prima unità del mazzo avversario e si danneggia di un valore pari alla relativa forza, poi la rimescola nel mazzo.\n"},132408:{name:"Punta-di-lancia",flavor:"Sei in un mare di guai, ora...",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},132409:{name:"Banchetto orribile",flavor:"Lo sciocco diede retta alle leggende, si turò le orecchie di cera e così non udì gli avvertimenti. E la sua nave si schiantò sulle rocce.",info:"Danneggia di 3 un nemico e potenzia di 3 un alleato.\n",infoRaw:"Danneggia di 3 un nemico e potenzia di 3 un alleato.\n"},133301:{name:"Scudo di viverna",flavor:"Più robusto dei normali scudi e molto più elegante.",info:"Fervore.\nOrdine: potenzia di 1 un'unità. \nRecupero: 1.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: potenzia di 1 un'unità. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},133302:{name:"Nido di arachas",flavor:"Dannazione, si stanno riproducendo... Ci serve un witcher, o sarà la fine!",info:"Evoca 4 Arachas fuchi con 1 di forza e li richiama su una fila alleata.\n",infoRaw:"<keyword=spawn>Evoca</keyword> 4 Arachas fuchi con 1 di forza e li <keyword=summon>richiama</keyword> su una fila alleata.\n"},142101:{name:"Saesenthessis",flavor:"Ho ereditato da mio padre la capacità di assumere altre forme... beh, una sola, nel mio caso.",info:"Immune.\nOrdine: perde l'attributo Immune per il resto del turno.\n",infoRaw:"<keyword=immune>Immune</keyword>.\n<keyword=order>Ordine</keyword>: perde l'attributo <keyword=immune>Immune</keyword> per il resto del turno.\n"},142102:{name:"Isengrim Faoiltiarna",flavor:"Quando vedono la mia cicatrice, capiscono subito che stanno per morire.",info:"Schieramento: potenzia di 1 tutti gli altri Elfi alleati.\nOgni volta che metti in gioco un Elfo, si potenzia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 tutti gli altri Elfi alleati.\nOgni volta che metti in gioco un Elfo, si potenzia di 1.\n"},142103:{name:"Iorveth",flavor:"Re o mendicante, che differenza fa? Un dh'oine in meno.",info:"Schieramento: riprendi in mano una Trappola alleata, poi metti in gioco una Trappola.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: riprendi in mano una Trappola alleata, poi metti in gioco una Trappola.\n"},142104:{name:"Milva",flavor:"Quando scocco una freccia, penso a mio padre. Mi piace pensare che sarebbe orgoglioso di me.",info:"Immune.\nOgni volta che metti in gioco un'unità Scoia'tael, si potenzia di 1.\n",infoRaw:"<keyword=immune>Immune</keyword>.\nOgni volta che metti in gioco un'unità Scoia'tael, si potenzia di 1.\n"},142105:{name:"Zoltan Chivay",flavor:"Bere da soli è come andare al bagno in gruppo.",info:"Schieramento, Prima fila: distrugge un'unità con forza non superiore al numero dei Nani alleati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un'unità con forza non superiore al numero dei Nani alleati.\n"},142106:{name:"Aglais",flavor:"Brokilon sanguina, ma neppure io posso curarla...",info:"Schieramento: alla fine del turno, raddoppia il valore dei potenziamenti che ha ricevuto.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: alla fine del turno, raddoppia il valore dei potenziamenti che ha ricevuto.\n"},142107:{name:"Ithlinne Aegli",flavor:"Celebre per le sue profezie sulla fine del mondo. Non è esattamente l'anima della festa.",info:"Schieramento: potenzia di 4 un'unità Scoia'tael nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 4 un'unità Scoia'tael nella tua mano.\n"},142108:{name:"Schirrú",flavor:"È tempo di guardare in faccia la morte.",info:"Fervore.\nOrdine: distrugge tutte le altre unità con lo stesso valore di forza di Schirrú.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: distrugge tutte le altre unità con lo stesso valore di forza di Schirrú.\n"},142201:{name:"Dennis Cranmer",flavor:"So come si esegue un ordine, quindi dispensa i tuoi consigli a qualcun altro.",info:"Schieramento, Prima fila: potenzia di 2 le unità adiacenti.\nSchieramento, Retrovie: potenzia di 1 tutte le altre unità su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: potenzia di 2 le unità adiacenti.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 1 tutte le altre unità su questa fila.\n"},142202:{name:"Ida Emean aep Sivney",flavor:"Sono una Saggia. Il mio potere risiede nel possedere la conoscenza, non nel condividerla.",info:"Schieramento, Prima fila: distrugge un manufatto.\nSchieramento, Retrovie: potenzia di 3 un'unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un manufatto.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 3 un'unità.\n"},142203:{name:"Yaevinn",flavor:"Siamo le gocce di pioggia che formano la tempesta.",info:"Schieramento: danneggia di 1 un nemico per ogni altro Elfo alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico per ogni altro Elfo alleato.\n"},142204:{name:"Toruviel",flavor:"Preferirei ucciderti guardandoti negli occhi. Ma puzzi troppo, umano.",info:"Schieramento, Prima fila: danneggia di 2 i nemici alle estremità di una fila.\nSchieramento, Retrovie: danneggia di 1 tutti i nemici di una fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 i nemici alle estremità di una fila.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia di 1 tutti i nemici di una fila.\n"},142205:{name:"Dragone Vrihedd",flavor:"Le cose più orribili che ho visto? La peste Catriona, la distruzione di Vengerberg e la carica dei Dragoni di Vrihedd.",info:"Schieramento, Prima fila: sposta un'unità nemica sull'altra sua fila.\nSchieramento, Retrovie: sposta un'unità alleata sull'altra sua fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: sposta un'unità nemica sull'altra sua fila.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: sposta un'unità alleata sull'altra sua fila.\n"},142206:{name:"Ciaran aep Easnillen",flavor:"Il cammino verso la libertà è tracciato col sangue, non con l'inchiostro.",info:"Schieramento: blocca un'unità e la sposta sull'altra sua fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=lock>blocca</keyword> un'unità e la sposta sull'altra sua fila.\n"},142207:{name:"Barclay Els",flavor:"Dici che il nostro idromele puzza? Nessun problema: ora ti rompo il naso!",info:"Schieramento: potenzia di 1 un alleato per ogni Nano alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 un alleato per ogni Nano alleato.\n"},142208:{name:"Morenn",flavor:"La figlia di Lady Eithné ereditò la sublime bellezza della madre insieme al suo odio per tutto ciò che è umano.",info:"Schieramento, Prima fila: danneggia di 2 un nemico.\nSchieramento, Retrovie: blocca un'unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 un nemico.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=lock>blocca</keyword> un'unità.\n"},142209:{name:"Braenn",flavor:"Mona...? No, no. Sono Braenn. Figlia di Brokilon.",info:"Schieramento: ottiene Fervore se controlli altre 2 o più Driadi.\nOrdine: danneggia di 1 un nemico.\nCariche: 3.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: ottiene <keyword=zeal>Fervore</keyword> se controlli altre 2 o più Driadi.\n<keyword=order>Ordine</keyword>: danneggia di 1 un nemico.\n<keyword=charge>Cariche</keyword>: 3.\n"},142210:{name:"Malena",flavor:"Ti odio, dh'oine. Per me siete tutti uguali.",info:"Fervore.\nOrdine: sposta un'unità sull'altra fila della sua metà di campo.\nRecupero: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: sposta un'unità sull'altra fila della sua metà di campo.\n<keyword=cooldown>Recupero</keyword>: 2.\n"},142211:{name:"Aelirenn",flavor:"Meglio morire a testa alta che vivere a capo chino.",info:"Se alla fine del turno controlli 5 o più Elfi alleati, richiama questa unità dal tuo mazzo sulla fila del combattimento corpo a corpo.\n",infoRaw:"Se alla fine del turno controlli 5 o più Elfi alleati, <keyword=summon>richiama</keyword> questa unità dal tuo mazzo sulla fila del combattimento corpo a corpo.\n"},142212:{name:"Sheldon Skaggs",flavor:"Ero là, al fronte! Dove infuriava la battaglia!",info:"Schieramento: danneggia un nemico di un valore pari alla forza di questa unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia un nemico di un valore pari alla forza di questa unità.\n"},142213:{name:"Yarpen Zigrin",flavor:"Mai sentito parlare del drago Ocvist? Della Montagna di Quarzo? Ebbene, a sconfiggerlo sono stati Yarpen Zigrin e la sua banda.",info:"Alla fine di ogni turno, se in campo non sono presenti unità alleate, si potenzia di 3.\n",infoRaw:"Alla fine di ogni turno, se in campo non sono presenti unità alleate, si potenzia di 3.\n"},142214:{name:"Ele'yas",flavor:"L'amore giustifica ogni forma di follia.",info:"Schieramento: danneggia di 1 tutte le unità danneggiate e potenzia di 1 tutte le unità potenziate.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 tutte le unità danneggiate e potenzia di 1 tutte le unità potenziate.\n"},142301:{name:"Curatrice Hav'caaren",flavor:"Certo che posso ricucirti, ma ti costerà.",info:"Schieramento, Prima fila: potenzia di 2 un'unità alleata.\nSchieramento, Retrovie: cura di 4 un'unità alleata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: potenzia di 2 un'unità alleata.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=heal>cura</keyword> di 4 un'unità alleata.\n"},142302:{name:"Brigata Vrihedd",flavor:"Vrihedd? Che significa? Guai.",info:"Schieramento: danneggia di 2 un nemico casuale.\nOgni volta che la Brigata Vrihedd viene spostata sull'altra fila, ripete l'abilità Schieramento.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico casuale.\nOgni volta che la Brigata Vrihedd viene spostata sull'altra fila, ripete l'abilità <keyword=deploy>Schieramento</keyword>.\n"},142303:{name:"Ufficiale Vrihedd",flavor:"L'odio brucia più del fuoco e ferisce più della spada.",info:"Schieramento, Prima fila: danneggia di 3 un nemico.\nSchieramento, Retrovie: potenzia di 3 un alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 3 un nemico.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 3 un alleato.\n"},142304:{name:"Dol Blathanna: bomba",flavor:"Cacciano come segugi, corrono come cervi e uccidono come demoni senza cuore.",info:"Schieramento: danneggia di 2 un nemico casuale su ogni fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico casuale su ogni fila.\n"},142305:{name:"Guerriero nanico",flavor:"Uso il piccone da una vita: l'ascia da guerra non mi darà problemi.",info:"Schieramento: danneggia di 3 un nemico. Se sopravvive, questa unità si potenzia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 3 un nemico. Se sopravvive, questa unità si potenzia di 1.\n"},142306:{name:"Difensore di Mahakam",flavor:"Sai, siamo nati per la battaglia: li falciamo alle ginocchia!",info:"Alla fine di ogni turno, si potenzia di 1 se questa unità è potenziata.\n",infoRaw:"Alla fine di ogni turno, si potenzia di 1 se questa unità è potenziata.\n"},142307:{name:"Genieri Vrihedd",flavor:"Le voci che girano sono infondate: gli elfi non fanno lo scalpo alle loro vittime umane. Troppi pidocchi.",info:"Schieramento: distrugge un manufatto nemico se hai un Elfo in mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico se hai un Elfo in mano.\n"},142308:{name:"Agguato",flavor:"Io sputo sugli ideali degli Scoia'tael, ma non sui loro soldi.",info:"Distrugge un'unità nemica casuale se in campo c'è un Elfo alleato.\n",infoRaw:"Distrugge un'unità nemica casuale se in campo c'è un Elfo alleato.\n"},142309:{name:"Avanguardia Vrihedd",flavor:"Temeriani, redaniani, fa lo stesso. Stanno meglio morti.",info:"Schieramento: si potenzia di 1 per ogni Elfo nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 1 per ogni Elfo nella tua mano.\n"},142310:{name:"Dol Blathanna: arco",flavor:"Un altro passo, dh'oine, e ti pianto una freccia in mezzo agli occhi.",info:"Schieramento, Prima fila: danneggia di 2 un nemico.\nSchieramento, Retrovie: danneggia 2 unità di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 un nemico.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia 2 unità di 1.\n"},142311:{name:"Mercenario nanico",flavor:"Il segreto della vita è unire l'utile al dilettevole... Come fare a pezzi i nemici ed essere pagati per questo.",info:"Ordine: danneggia di 1 un nemico. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che metti in gioco un Nano.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un nemico. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che metti in gioco un Nano.\n"},142312:{name:"Aiutante Hav'caaren",flavor:"Elfi, nani, per me non fa differenza. Purché paghino.",info:"Schieramento: se controlli un manufatto, si potenzia di 3, altrimenti potenzia un alleato di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se controlli un manufatto, si potenzia di 3, altrimenti potenzia un alleato di 1.\n"},142313:{name:"Elfa danzatrice di guerra",flavor:"Vuoi farmi credere che l'elfa si è messa a danzare in mezzo alla battaglia? Sei impazzito, caporale?",info:"Schieramento: se questa unità è già potenziata, danneggia di 3 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se questa unità è già potenziata, danneggia di 3 un nemico.\n"},142314:{name:"Dol Blathanna: saetta",flavor:"Puoi provare a nasconderti, ma se ti vedono non ha senso fuggire.",info:"Schieramento: danneggia di 1 un nemico per ogni fila che lo separa da questa unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico per ogni fila che lo separa da questa unità.\n"},142315:{name:"Affarista Hav'caaren",flavor:"Io combatto per il miglior offerente, o per chi è più facile da fregare.",info:"Prima fila: alla fine di ogni turno, potenzia di 1 un'unità casuale nella tua mano.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: alla fine di ogni turno, potenzia di 1 un'unità casuale nella tua mano.\n"},142316:{name:"Monti blu: élite",flavor:"Quando li abbiamo sentiti, era ormai troppo tardi...",info:"Schieramento: danneggia di 4 un nemico se non ci sono altre unità sulla sua fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico se non ci sono altre unità sulla sua fila.\n"},142317:{name:"Guardia di Mahakam",flavor:"C'è un solo modo per punire chi turba la pace a Mahakam: una bella martellata nei denti.",info:"Schieramento: si potenzia di un valore pari al numero di Nani alleati presenti sull'altra sua fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di un valore pari al numero di Nani alleati presenti sull'altra sua fila.\n"},142401:{name:"Neofita Scoia'tael",flavor:"Molti non-umani, stanchi del razzismo e della xenofobia delle città, decidono di unirsi agli Scoia'tael.",info:"Schieramento: evoca e richiama una copia base di questa unità sull'altra fila alleata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> e <keyword=summon>richiama</keyword> una copia base di questa unità sull'altra fila alleata.\n"},143201:{name:"Trappola di serpenti",flavor:"Come osate depredare la madre Terra? Un tempo, i suoi frutti crescevano rigogliosi come dono per la nostra sacra devozione.",info:"Imboscata: distrugge la prossima carta Speciale giocata dall'avversario prima che se ne attivino le abilità.\n",infoRaw:"<keyword=ambush>Imboscata</keyword>: distrugge la prossima carta Speciale giocata dall'avversario prima che se ne attivino le abilità.\n"},143301:{name:"Trappola incendiaria",flavor:"Prudenza! Un altro passo e ti scioglierai come neve al sole.",info:"Imboscata: danneggia di 5 la prossima unità messa in gioco dall'avversario. \nFervore.\nOrdine: gira questa carta e danneggia di 3 un'unità.\n",infoRaw:"<keyword=ambush>Imboscata</keyword>: danneggia di 5 la prossima unità messa in gioco dall'avversario. \n<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: gira questa carta e danneggia di 3 un'unità.\n"},152101:{name:"Hjalmar an Craite",flavor:"Invece di piangere i caduti, beviamo alla loro memoria!",info:"Schieramento, Prima fila: bandisce un'unità nel tuo cimitero e danneggia un nemico di un valore pari alla relativa forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=banish>bandisce</keyword> un'unità nel tuo cimitero e danneggia un nemico di un valore pari alla relativa forza.\n"},152103:{name:"Ermellino",flavor:"Solo gli ignoranti sottovalutano l'importanza dei miti.",info:"Schieramento, Retrovie: gioca una carta Alchimia dal tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: gioca una carta Alchimia dal tuo mazzo.\n"},152104:{name:"Kambi",flavor:"Quando il giorno verrà, il gallo Kambi canterà per risvegliare Hemdall.",info:"Schieramento: scarta la carta all'estrema destra della tua mano, dopodiché l'avversario scarta la carta all'estrema destra della sua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=discard>scarta</keyword> la carta all'estrema destra della tua mano, dopodiché l'avversario <keyword=discard>scarta</keyword> la carta all'estrema destra della sua mano.\n"},152105:{name:"Birna Bran",flavor:"Skellige deve avere un re forte. A qualsiasi costo.",info:"Schieramento: pesca 2 carte, poi scarta 2 carte.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: pesca 2 carte, poi <keyword=discard>scarta</keyword> 2 carte.\n"},152106:{name:"Lugos il Pazzo",flavor:"AAAAARGH!",info:"Schieramento: danneggia un nemico di un valore pari al doppio dei nemici danneggiati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia un nemico di un valore pari al doppio dei nemici danneggiati.\n"},152107:{name:"Coral",flavor:"Il suo vero nome è Astrid Lyttneyd Ásgeirrfinnbjornsdottir, ma non c'è mai spazio sufficiente sui moduli.",info:"Ordine: scarta una carta, poi pesca una carta. \nCariche: 3.\nOgni volta che scarti una carta Speciale, danneggia di 2 un nemico.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=discard>scarta</keyword> una carta, poi pesca una carta. \n<keyword=charge>Cariche</keyword>: 3.\nOgni volta che <keyword=discard>scarti</keyword> una carta Speciale, danneggia di 2 un nemico.\n"},152109:{name:"Cinghiale marino",flavor:"Ne basta il nome e i nilfgaardiani se la fanno addosso...",info:"Schieramento, Prima fila: danneggia di 2 tutti i nemici danneggiati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 tutti i nemici danneggiati.\n"},152201:{name:"Lugos il Blu",flavor:"Mi sento male dalla noia.",info:"Ogni volta che questa unità subisce danni, danneggia di 2 un'unità nemica casuale.\n",infoRaw:"Ogni volta che questa unità subisce danni, danneggia di 2 un'unità nemica casuale.\n"},152202:{name:"Carica del campione",flavor:"A distanza ravvicinata, i troll non usano armi. In effetti, perché dovrebbero?",info:"Danneggia di 5 un'unità. \nSete di sangue 3: distrugge un'unità.\n",infoRaw:"Danneggia di 5 un'unità. \n<keyword=bloodthirst>Sete di sangue</keyword> 3: distrugge un'unità.\n"},152203:{name:"Djenge Frett",flavor:"Quando la taglia dice vivo o morto, i cacciatori di taglie di solito uccidono in fretta. Non nel mio caso. Se ti prendo, ti impicco e ti faccio il solletico sotto i piedi finché non muori.",info:"Sete di sangue 1: blocca un'unità nemica.\n",infoRaw:"<keyword=bloodthirst>Sete di sangue</keyword> 1: <keyword=lock>blocca</keyword> un'unità nemica.\n"},152204:{name:"Donar an Hindar",flavor:"Ho radunato tutti gli jarl. Ora tocca a te.",info:"Schieramento: danneggia di 2 un nemico.\nSete di sangue 2: danneggia di 5 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico.\n<keyword=bloodthirst>Sete di sangue</keyword> 2: danneggia di 5 un nemico.\n"},152205:{name:"Draig Bon-Dhu",flavor:"Ora ascolterai le grandi gesta del clan an Craite.",info:"Schieramento: potenzia di 1 tutti i Guerrieri alleati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 tutti i Guerrieri alleati.\n"},152206:{name:"Gremist",flavor:"Arcidruido, maestro di alchimia nonché il peggior brontolone delle Isole.",info:"Ordine, Retrovie: gioca una carta Alchimia Bronzo dal tuo cimitero.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: gioca una carta Alchimia Bronzo dal tuo cimitero.\n"},152207:{name:"Holger Manonera",flavor:"Brindiamo all'Imperatore di Nilfgaard: che possa morire tra atroci sofferenze!",info:"Ogni volta che metti in gioco una Nave, la potenzia di 1.\nOgni volta che metti in gioco un Pirata, danneggia di 1 un nemico casuale.\n",infoRaw:"Ogni volta che metti in gioco una Nave, la potenzia di 1.\nOgni volta che metti in gioco un Pirata, danneggia di 1 un nemico casuale.\n"},152208:{name:"Jutta an Dimun",flavor:"Alcuni la chiamano Vergine di ferro.",info:"Schieramento: si danneggia di 6 se Jutta an Dimun è l'unità più forte.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si danneggia di 6 se Jutta an Dimun è l'unità più forte.\n"},152209:{name:"Morkvarg",flavor:"L'uomo più vile di Skellige.",info:"Se questa unità finisce nel cimitero durante il round, viene richiamata nella fila del combattimento corpo a corpo e ottiene l'attributo Condannata.\n",infoRaw:"Se questa unità finisce nel cimitero durante il round, viene <keyword=summon>richiamata</keyword> nella fila del combattimento corpo a corpo e ottiene l'attributo <keyword=doomed>Condannata</keyword>.\n"},152210:{name:"Orso selvaggio",flavor:"Addomesticare? Senti, a Skellige si addestrano gli orsi, certo, ma non è la stessa cosa che addomesticarli...",info:"Fervore.\nSchieramento: aggiunge 1 alle proprie Cariche per ogni Orso selvaggio presente nel cimitero.\nOrdine: danneggia di 2 un'unità.\nCariche: 1.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=deploy>Schieramento</keyword>: aggiunge 1 alle proprie <keyword=charge>Cariche</keyword> per ogni Orso selvaggio presente nel cimitero.\n<keyword=order>Ordine</keyword>: danneggia di 2 un'unità.\n<keyword=charge>Cariche</keyword>: 1.\n"},152211:{name:"Rito di Sigrdrifa",flavor:"Inginocchiatevi e implorate il perdono di Modron Freya.",info:"Richiama un Guerriero dal tuo cimitero su una fila alleata.\n",infoRaw:"<keyword=summon>Richiama</keyword> un Guerriero dal tuo cimitero su una fila alleata.\n"},152213:{name:"Svanrige Tuirseach",flavor:"L'Imperatore pensava di poter manipolare il re. All'inizio.",info:"Schieramento, Sete di sangue 3: si potenzia di 5.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=bloodthirst>Sete di sangue</keyword> 3: si potenzia di 5.\n"},152214:{name:"Udalryk an Brokvar",flavor:"Gli dei hanno parlato: occorre un sacrificio.",info:"Ordine: danneggia di 1 un'unità.\nCariche: 3.\nSete di sangue 2: ottiene Fervore.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità.\n<keyword=charge>Cariche</keyword>: 3.\n<keyword=bloodthirst>Sete di sangue</keyword> 2: ottiene <keyword=zeal>Fervore</keyword>.\n"},152303:{name:"An Craite: guerriero",flavor:"I bardi del nostro clan canteranno ancora le mie imprese quando di te non sarà rimasta più alcuna traccia!",info:"Schieramento: danneggia di 4 un nemico se viene messa in gioco dal cimitero, altrimenti lo danneggia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico se viene messa in gioco dal cimitero, altrimenti lo danneggia di 2.\n"},152304:{name:"Brokvar: cacciatore",flavor:"Certo che sappiamo cacciare. Ma non c'è molta selvaggina a Spikeroog...",info:"Ordine: danneggia di 1 un'unità. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che scarti una carta.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che <keyword=discard>scarti</keyword> una carta.\n"},152305:{name:"Dimun: pirata",flavor:"Leggo la paura nei loro occhi. Hanno paura di me... e del clan Dimun!",info:"Schieramento: sposta nel tuo cimitero la prima carta del tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: sposta nel tuo cimitero la prima carta del tuo mazzo.\n"},152306:{name:"Dimun: capitano",flavor:"Tutti uniti, issate la nostra bandiera!",info:"Schieramento.\nSete di sangue 2: danneggia di 3 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>.\n<keyword=bloodthirst>Sete di sangue</keyword> 2: danneggia di 3 un nemico.\n"},152307:{name:"Drummond: guardia",flavor:"La regina è protetta dalle guerriere più fiere e indomite di Skellige.",info:"Ordine: se questa unità viene danneggiata, evocane una copia base e richiamala alla sua destra.\n",infoRaw:"<keyword=order>Ordine</keyword>: se questa unità viene danneggiata, <keyword=spawn>evocane</keyword> una copia base e <keyword=summon>richiamala</keyword> alla sua destra.\n"},152308:{name:"Heymaey: bardo",flavor:"Le imprese del clan Heymaey faranno la storia.",info:"Schieramento: scarta una carta, poi pesca una carta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=discard>scarta</keyword> una carta, poi pesca una carta.\n"},152309:{name:"Dimun: nave leggera",flavor:"Seminarla? Nelle acque di Skellige? Buona fortuna.",info:"Ordine: danneggia un nemico e se stessa di 1. \nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia un nemico e se stessa di 1. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},152310:{name:"Benedizione di Freya",flavor:"Modron Freya, la Grande Madre, è la dea dell'amore, della bellezza e dell'abbondanza.",info:"Metti in gioco un'unità Bronzo dal tuo cimitero.\n",infoRaw:"Metti in gioco un'unità Bronzo dal tuo cimitero.\n"},152311:{name:"An Craite: fabbro",flavor:"Rammenta le mie parole: un buono scudo può salvarti la vita.",info:"Ordine: potenzia di 1 un alleato. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che metti in gioco un Guerriero.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 1 un alleato. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che metti in gioco un Guerriero.\n"},152312:{name:"Tuirseach: asciere",flavor:"Le spade sono da donnette. Procurati un'ascia.",info:"Schieramento, Prima fila: distrugge un nemico la cui forza è non superiore alla metà della forza base.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un nemico la cui forza è non superiore alla metà della forza base.\n"},152313:{name:"Tuirseach: guerriero",flavor:"Agli amici offriamo il cuore, ai nemici l'ascia. Ricordalo.",info:"Quando questa unità viene scartata, richiamala dal tuo cimitero sulla fila del combattimento corpo a corpo.\n",infoRaw:"Quando questa unità viene <keyword=discard>scartata</keyword>, <keyword=summon>richiamala</keyword> dal tuo cimitero sulla fila del combattimento corpo a corpo.\n"},152314:{name:"An Craite: nave",flavor:"Dicono che il cuore di Hemdall palpiti ogni volta che le navi compiono una razzia.",info:"Ogni volta che l'avversario mette in gioco un'unità, la danneggia di 1.\n",infoRaw:"Ogni volta che l'avversario mette in gioco un'unità, la danneggia di 1.\n"},152315:{name:"Brokvar: arciere",flavor:"Sai colpire un bersaglio in movimento a duecento passi? Anch'io. E in una tempesta.",info:"Schieramento, Retrovie: danneggia un nemico di un valore pari al numero delle unità danneggiate nella metà di campo avversaria.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia un nemico di un valore pari al numero delle unità danneggiate nella metà di campo avversaria.\n"},152316:{name:"Taglio sventrante",flavor:"Uomo o albero, per l'ascia di un abitante di Skellige non fa differenza.",info:"Danneggia di 4 un'unità. \nSete di sangue 2: danneggia di 6 un'unità.\n",infoRaw:"Danneggia di 4 un'unità. \n<keyword=bloodthirst>Sete di sangue</keyword> 2: danneggia di 6 un'unità.\n"},152317:{name:"An Craite: armaiolo",flavor:"Ti meriti una lezione.",info:"Schieramento: se un alleato è un Guerriero, lo potenzia di 3, altrimenti lo potenzia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se un alleato è un Guerriero, lo potenzia di 3, altrimenti lo potenzia di 1.\n"},152318:{name:"Drummond: guerriera",flavor:"Si infrangeranno sui nostri scudi come onde sugli scogli!",info:"La prossima volta che questa unità viene danneggiata, richiamane una copia dal tuo mazzo su questa fila.\n",infoRaw:"La prossima volta che questa unità viene danneggiata, <keyword=summon>richiamane</keyword> una copia dal tuo mazzo su questa fila.\n"},152402:{name:"Hemdall",flavor:"Quando verrà il Bianco Gelo, Hemdall darà inizio alla battaglia.",info:"Prima fila: ogni volta che un Guerriero alleato danneggia un nemico, potenzia questa unità di un valore pari ai danni inflitti.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: ogni volta che un Guerriero alleato danneggia un nemico, potenzia questa unità di un valore pari ai danni inflitti.\n"},152403:{name:"Delirio",flavor:"Troppo grande per essere un capodoglio. Che sia una balenottera? Cosa? Con quel muso così schiacciato? Le erbe ti hanno dato alla testa!",info:"Danneggia casualmente tutte le unità su una fila nemica per un valore complessivo di 6.\n",infoRaw:"Danneggia casualmente tutte le unità su una fila nemica per un valore complessivo di 6.\n"},152405:{name:"Orso anziano",flavor:"A caccia dell'orso andiamo, di un orso grande e... MAMMA, QUANT'È GROSSO! VIA!",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},152406:{name:"Orso furioso",flavor:"Grrr!!!",info:"Schieramento: danneggia di 1 un alleato alla sua sinistra. Se non ce ne sono, si danneggia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un alleato alla sua sinistra. Se non ce ne sono, si danneggia di 1.\n"},153201:{name:"Rivitalizzazione",flavor:"Quelle dannate maghe ci rubano il lavoro! Perché prediligere un'operazione lunga e rischiosa, quando una bella fanciulla può risolvere il problema con uno schiocco di dita?",info:"Cura tutte le unità di una fila.\n",infoRaw:"<keyword=heal>Cura</keyword> tutte le unità di una fila.\n"},153301:{name:"Smembramento",flavor:"Una vista orribile... Il corpo giaceva lì, fatto a pezzi, mentre la bestia si nutriva del suo sangue sparso sul terreno circostante.",info:"Danneggia di 2 tutte le unità di una fila.\n",infoRaw:"Danneggia di 2 tutte le unità di una fila.\n"},162101:{name:"Letho di Gulet",flavor:"I witcher non muoiono nei loro letti.",info:'"Beh, il mio piano sta andando a rotoli, farò meglio a sparire."\n(L\'abilità di Letho tornerà presto.)\n',infoRaw:'"Beh, il mio piano sta andando a rotoli, farò meglio a sparire."\n(L\'abilità di Letho tornerà presto.)\n'},162102:{name:"Menno Coehoorn",flavor:"Preferisco mille volte una buona unità di ricognizione a una buona brigata.",info:"Schieramento, Retrovie: gioca una carta Strategia dal tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: gioca una carta Strategia dal tuo mazzo.\n"},162103:{name:"Vattier de Rideaux",flavor:"Non esiste problema che non si possa risolvere con un assassinio ben pianificato.",info:"Ordine, Prima fila: sequestra un nemico bloccato.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=seize>sequestra</keyword> un nemico <keyword=lock>bloccato</keyword>.\n"},162104:{name:"Cahir Dyffryn",flavor:"I suoi occhi brillavano sotto l'elmo alato e la sua spada riluceva infuocata.",info:"Prima fila: ogni volta che un nemico viene potenziato, si potenzia della stessa quantità.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: ogni volta che un nemico viene potenziato, si potenzia della stessa quantità.\n"},162105:{name:"Vilgefortz",flavor:"Non siamo che pedine sulla sua scacchiera. Partecipiamo a un gioco di cui non conosciamo le regole.",info:"Schieramento, Prima fila: distrugge un'unità nemica, dopodiché l'avversario richiama la prima carta del suo mazzo su un fila nemica casuale.\nSchieramento, Retrovie: distrugge un'unità alleata, dopodiché richiama la prima carta del tuo mazzo su una fila alleata casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un'unità nemica, dopodiché l'avversario <keyword=summon>richiama</keyword> la prima carta del suo mazzo su un fila nemica casuale.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: distrugge un'unità alleata, dopodiché <keyword=summon>richiama</keyword> la prima carta del tuo mazzo su una fila alleata casuale.\n"},162106:{name:"Stefan Skellen",flavor:"La cicatrice sul volto della futura imperatrice è opera mia, e ne vado fiero.",info:"Ordine: se questa unità è in campo, evoca e gioca una copia di una carta Strategia giocata durante questo turno.\n",infoRaw:"<keyword=order>Ordine</keyword>: se questa unità è in campo, <keyword=spawn>evoca</keyword> e gioca una copia di una carta Strategia giocata durante questo turno.\n"},162107:{name:"Tibor Eggebracht",flavor:"La devozione di Tibor era leggendaria: davanti all'imperatore s'inchinava così tanto che il suo naso toccava terra.",info:"Schieramento: l'avversario richiama la prima unità del suo mazzo su una fila nemica casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: l'avversario <keyword=summon>richiama</keyword> la prima unità del suo mazzo su una fila nemica casuale.\n"},162108:{name:"Xarthisius",flavor:"Astrologia, idromanzia, aruspicina, ceromanzia. Ovomanzia, cineromanzia, metroscopia, brontoscopia...",info:"Schieramento: svela una carta casuale del tuo mazzo. Se è un'unità, la rimescola nel mazzo e si potenzia di un valore pari alla relativa forza. Se invece è un manufatto o una carta Speciale, giocala.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=reveal>svela</keyword> una carta casuale del tuo mazzo. Se è un'unità, la rimescola nel mazzo e si potenzia di un valore pari alla relativa forza. Se invece è un manufatto o una carta Speciale, giocala.\n"},162201:{name:"Albrich",flavor:"Una palla di fuoco? Oh, ma certo. Come Vostra Altezza Imperiale desidera!",info:"Schieramento, Retrovie: sposta una carta del tuo mazzo in cima al mazzo. Se è un'unità, potenziala di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: sposta una carta del tuo mazzo in cima al mazzo. Se è un'unità, potenziala di 2.\n"},162202:{name:"Assire var Anahid",flavor:"I maghi di Nilfgaard possono scegliere tra la sottomissione e la forca.",info:"Schieramento, Prima fila: rimescola una carta del cimitero avversario nel relativo mazzo.\nSchieramento, Retrovie: rimescola una carta del tuo cimitero nel tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: rimescola una carta del cimitero avversario nel relativo mazzo.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: rimescola una carta del tuo cimitero nel tuo mazzo.\n"},162203:{name:"Cynthia",flavor:"I talenti di Cynthia possono essere letali. Va tenuta d'occhio.",info:"Schieramento, Prima fila: evoca Il Guardiano e mettilo in cima al mazzo avversario.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=spawn>evoca</keyword> Il Guardiano e mettilo in cima al mazzo avversario.\n"},162204:{name:"Peter Saar Gwynleve",flavor:"Queste non sono le mani di un nobile, ma di un contadino. Perciò parliamo da contadino a contadino.",info:"Schieramento: riporta un'unità alla sua forza base.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: riporta un'unità alla sua forza base.\n"},162205:{name:"Fringilla Vigo",flavor:"La magia è il bene più importante. Trascende ogni confine o differenza.",info:"Ordine: svela una carta Speciale a caso presente nel mazzo dell'avversario, poi evoca e giocane una copia.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=reveal>svela</keyword> una carta Speciale a caso presente nel mazzo dell'avversario, poi <keyword=spawn>evoca</keyword> e giocane una copia.\n"},162206:{name:"Sweers",flavor:"E giù le mani dalla ragazza! Non siamo mica dei selvaggi.",info:"Schieramento: danneggia di 1 un nemico.\nOgni volta che sveli una carta, aumenta di 1 i danni di Schieramento di Sweers finché non viene rimosso dal campo di battaglia.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico.\nOgni volta che <keyword=reveal>sveli</keyword> una carta, aumenta di 1 i danni di <keyword=deploy>Schieramento</keyword> di Sweers finché non viene rimosso dal campo di battaglia.\n"},162207:{name:"Vanhemar",flavor:"Per essere un mago del fuoco, non è proprio... caloroso.",info:"Schieramento, Retrovie: distrugge un'unità nemica bloccata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: distrugge un'unità nemica <keyword=lock>bloccata</keyword>.\n"},162208:{name:"Auckes",flavor:"Letho ha un piano. Cosa potrebbe andare storto?",info:"Schieramento: blocca un'unità nemica.\nSe hai in mano Serrit, blocca tutte le copie di quell'unità nella mano, nel mazzo e nella metà di campo dell'avversario.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=lock>blocca</keyword> un'unità nemica.\nSe hai in mano Serrit, <keyword=lock>blocca</keyword> tutte le copie di quell'unità nella mano, nel mazzo e nella metà di campo dell'avversario.\n"},162209:{name:"Serrit",flavor:"Facciamo ciò che va fatto. E non me ne vergogno.",info:"Schieramento: se hai in mano Auckes, danneggia di 5 un nemico, altrimenti lo danneggia di 3.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se hai in mano Auckes, danneggia di 5 un nemico, altrimenti lo danneggia di 3.\n"},162210:{name:"Cantarella",flavor:"Bisogna saper ammaliare gli uomini. Mistero ed eleganza sono le mie armi.",info:"Spia.\nSchieramento: gioca la prima carta del mazzo avversario.\n",infoRaw:"<keyword=spying>Spia</keyword>.\n<keyword=deploy>Schieramento</keyword>: gioca la prima carta del mazzo avversario.\n"},162211:{name:"Joachim de Wett",flavor:"Dire che il Duca de Wett comandava l'armata di Vergen da incompetente sarebbe un eufemismo.",info:"Spia.\nSchieramento: gioca la prima carta del tuo mazzo non appartenente alla categoria Spia e potenziala di 8.\n",infoRaw:"<keyword=spying>Spia</keyword>.\n<keyword=deploy>Schieramento</keyword>: gioca la prima carta del tuo mazzo non appartenente alla categoria <keyword=spying>Spia</keyword> e potenziala di 8.\n"},162212:{name:"Falsa Ciri",flavor:"Ecco che arriva l'usurpatrice, pensò lui. Falsa principessa, falsa regina di Cintra, falsa sovrana della foce di Yarra, ma futura linfa vitale dell'Impero.",info:"Schieramento, Prima fila: sposta un nemico sull'altra sua fila.\nSchieramento, Retrovie: distrugge un manufatto nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: sposta un nemico sull'altra sua fila.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: distrugge un manufatto nemico.\n"},162213:{name:"Ceallach Dyffryn",flavor:"Vostra Altezza Imperiale... esclamò il siniscalco, ignorato fino a quel momento. Chiedo venia, ma Cahir, mio figlio...",info:"Schieramento: rimuove gli stati da tutte le unità in campo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: rimuove gli stati da tutte le unità in campo.\n"},162301:{name:"Soldato di Daerlan",flavor:"Ho imparato molto all'Accademia. A pelare patate, per esempio.",info:"Ogni volta che sveli questa unità, richiamala dal mazzo su una fila alleata casuale.\n",infoRaw:"Ogni volta che <keyword=reveal>sveli</keyword> questa unità, <keyword=summon>richiamala</keyword> dal mazzo su una fila alleata casuale.\n"},162302:{name:"Untore",flavor:"Che gli storici dibattano pure sull'eticità del diffondere una pestilenza in una città assediata. Quello che conta è il risultato.",info:"Schieramento, Retrovie: evoca e metti in gioco una Carcassa di mucca.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=spawn>evoca</keyword> e metti in gioco una Carcassa di mucca.\n"},162303:{name:"Osservatore",flavor:"Il Nord non ha nulla che possa sorprenderci.",info:"Schieramento, Prima fila: svela un'unità casuale del mazzo dell'avversario e si potenzia di un valore pari alla relativa forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=reveal>svela</keyword> un'unità casuale del mazzo dell'avversario e si potenzia di un valore pari alla relativa forza.\n"},162304:{name:"Rimedio sperimentale",flavor:"Beh, se non altro... più di così non puoi morire...",info:"Metti in gioco un'unità Bronzo dal cimitero dell'avversario.\n",infoRaw:"Metti in gioco un'unità Bronzo dal cimitero dell'avversario.\n"},162305:{name:"Arbalesta Deithwen",flavor:"Miro alle ginocchia. Sempre.",info:"Schieramento: danneggia di 1 un nemico, poi svela un'unità casuale di entrambi i mazzi. Se la tua è più forte, ripete l'abilità Schieramento.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico, poi <keyword=reveal>svela</keyword> un'unità casuale di entrambi i mazzi. Se la tua è più forte, ripete l'abilità <keyword=deploy>Schieramento</keyword>.\n"},162306:{name:"Scorpione di fuoco",flavor:"Il nome è alquanto fuorviante, poiché lo accomuna più a un corpulento aracnide rossastro, mentre in verità è un'arma di distruzione di massa squisitamente sofisticata...",info:"Ordine, Retrovie: danneggia di 1 un'unità. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che giochi una carta Strategia.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che giochi una carta Strategia.\n"},162307:{name:"Brigata Impera",flavor:"La brigata Impera non si arrende. Mai.",info:"Schieramento, Prima fila: richiama una copia di questa unità dal tuo mazzo su questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=summon>richiama</keyword> una copia di questa unità dal tuo mazzo su questa fila.\n"},162308:{name:"Guardie Impera",flavor:"I membri della guardia del corpo dell'imperatore sono fanatici che lottano fino alla morte.",info:"Ordine: danneggia di 2 un nemico.\nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che metti in gioco un'unità Spia.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 2 un nemico.\n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che metti in gioco un'unità <keyword=spying>Spia</keyword>.\n"},162309:{name:"Sergente Nauzicaa",flavor:"L'imperatore darà una lezione al Nord.",info:"Si potenzia di 1 ogni volta che metti in gioco un'unità dotata di Schieramento.\n",infoRaw:"Si potenzia di 1 ogni volta che metti in gioco un'unità dotata di <keyword=deploy>Schieramento</keyword>.\n"},162310:{name:"Brigata Nauzicaa",flavor:"Ci chiamano Teste di Morte. Vuoi scoprire il perché?",info:"Schieramento: danneggia di 3 un nemico potenziato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 3 un nemico potenziato.\n"},162311:{name:"Picchiere Alba",flavor:"Giura fedeltà all'imperatore Emhyr var Emreis... oppure muori.",info:"Schieramento, Prima fila: danneggia un nemico di un valore pari al numero delle unità presenti sull'altra sua fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia un nemico di un valore pari al numero delle unità presenti sull'altra sua fila.\n"},162312:{name:"Lanciere Alba",flavor:"Morte e gloria!",info:"Schieramento: danneggia di 2 un nemico. Aumenta di 1 i danni per ogni Soldato adiacente.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico. Aumenta di 1 i danni per ogni Soldato adiacente.\n"},162313:{name:"Ingegnere bellico",flavor:"Vuoi una prova del progresso della civiltà? Pensa alla guerra. Gli uomini si uccidono in modi sempre più efficaci.",info:"Schieramento: si potenzia di 3 se controlli un manufatto.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 3 se controlli un manufatto.\n"},162314:{name:"Emissario",flavor:"Ma... è un'ingiustizia! Ambasciator non porta pena!",info:"Spia.\nSchieramento: potenzia di 7 un alleato.\n",infoRaw:"<keyword=spying>Spia</keyword>.\n<keyword=deploy>Schieramento</keyword>: potenzia di 7 un alleato.\n"},162315:{name:"Diplomazia imperiale",flavor:"Una spia? Non esageriamo. Mi considero più un fine osservatore.",info:"Crea e gioca una carta Bronzo della fazione avversaria.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo della fazione avversaria.\n"},162316:{name:"Alchimista",flavor:"Due once di calcium equum, un'oncia di rubedo...",info:"Schieramento, Retrovie: scambia la forza di 2 alleati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: scambia la forza di 2 alleati.\n"},162317:{name:"Manganella",flavor:"La sua specialità è lanciare cadaveri e sterco fumante.",info:"Ogni volta che sveli una carta, danneggia di 1 un nemico casuale.\n",infoRaw:"Ogni volta che <keyword=reveal>sveli</keyword> una carta, danneggia di 1 un nemico casuale.\n"},162318:{name:"Nilfgaard: cavaliere",flavor:"Di nobili origini e nati nella città delle torri d'oro, sono la prestigiosa spina dorsale dell'esercito imperiale.",info:"Schieramento: potenzia di 2 un nemico. Se non ci sono unità nemiche, si distrugge.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un nemico. Se non ci sono unità nemiche, si distrugge.\n"},162401:{name:"Il Guardiano",flavor:"Pugni di pietra per fermare gli intrusi armati di spada. Logica ferrea per fermare quelli armati di menzogne.",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},162402:{name:"Carcassa di mucca",flavor:"È un grifone? È un uccello? No, è...",info:"Spia. Condannata.\nDopo 1 turno, alla fine del turno, distrugge sé e l'unità alla sua sinistra.\n",infoRaw:"<keyword=spying>Spia</keyword>. <keyword=doomed>Condannata</keyword>.\nDopo 1 turno, alla fine del turno, distrugge sé e l'unità alla sua sinistra.\n"},163101:{name:"Tradimento",flavor:"A Nilfgaard, le persone di cui ci si può fidare sono una specie a rischio.",info:"Costringe un'unità nemica a danneggiare le unità adiacenti di un valore pari alla sua forza.\n",infoRaw:"Costringe un'unità nemica a danneggiare le unità adiacenti di un valore pari alla sua forza.\n"},163201:{name:"Corruzione",flavor:"Molti credono che la vera forza dell'Impero risieda in un esercito ben disciplinato e maghi potenti. In realtà, a dominare il mondo è il fiorino nilfgaardiano.",info:"Crea un'unità proveniente dal mazzo di partenza dell'avversario, poi mettila in gioco.\n",infoRaw:"<keyword=create>Crea</keyword> un'unità proveniente dal mazzo di partenza dell'avversario, poi mettila in gioco.\n"},200008:{name:"Filtro di Petri",flavor:"Un mago responsabile di decine di invenzioni rivoluzionarie. La mente più brillante della sua generazione. Sul Continente è noto come il tizio con l'ampolla.",info:"Fervore.\nOrdine: potenzia di 3 un alleato e gli fornisce Fervore.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: potenzia di 3 un alleato e gli fornisce <keyword=zeal>Fervore</keyword>.\n"},200018:{name:"Canicola del Korath",flavor:"Gli studiosi di Vicovaro hanno previsto che, in assenza di aiuti imperiali, le province colpite dalla siccità perderanno metà della popolazione, due terzi del bestiame e tutta la forza di ribellarsi.",info:"Bandisce un'unità o un manufatto.\n",infoRaw:"<keyword=banish>Bandisce</keyword> un'unità o un manufatto.\n"},200019:{name:"Marcia",flavor:"Siamo soltanto pedine in una partita giocata da uomini anziani, inviati a combattere e morire per i loro capricci senili...",info:"Metti in gioco l'unità meno forte del tuo mazzo.\n",infoRaw:"Metti in gioco l'unità meno forte del tuo mazzo.\n"},200020:{name:"Negromanzia",flavor:"Abbiamo i nostri modi per farti parlare... vivo o morto.",info:"Metti in gioco un'unità Bronzo dal tuo cimitero e assegnale l'attributo Condannata.\n",infoRaw:"Metti in gioco un'unità Bronzo dal tuo cimitero e assegnale l'attributo <keyword=doomed>Condannata</keyword>.\n"},200022:{name:"Circolo d'evocazione",flavor:"Esistono molte realtà all'infuori della nostra... Con le giuste conoscenze, è possibile contattarle ed evocare esseri che si spingono oltre la comprensione umana.",info:"Fervore.\nOrdine: mette in gioco un'unità dal tuo mazzo con un costo di reclutamento non superiore alle Cariche di Circolo d'evocazione, poi perde tutte le Cariche.\nCariche: 0.\nAll'inizio di ogni turno, aggiunge 1 alle proprie Cariche.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: mette in gioco un'unità dal tuo mazzo con un costo di reclutamento non superiore alle <keyword=charge>Cariche</keyword> di Circolo d'evocazione, poi perde tutte le <keyword=charge>Cariche</keyword>.\n<keyword=charge>Cariche</keyword>: 0.\nAll'inizio di ogni turno, aggiunge 1 alle proprie <keyword=charge>Cariche</keyword>.\n"},200023:{name:"Veleno di arachas",flavor:"In caso di contatto con gli occhi, risciacquare immediatamente con acqua fredda e fare testamento.",info:"Danneggia di 3 un'unità. Se viene distrutta, danneggia di 3 le unità adiacenti a essa.\n",infoRaw:"Danneggia di 3 un'unità. Se viene distrutta, danneggia di 3 le unità adiacenti a essa.\n"},200026:{name:"Caccia: navigatrice",flavor:"Per secoli, Avallac'h ha cercato di ricreare il gene dei Sangue Ancestrale tramite selezione artificiale. Tuttavia, nonostante i suoi sforzi, i bambini elfici così ottenuti non erano altro che tiepide braci, se paragonati alla vivida fiamma di Lara.",info:"Schieramento: se controlli l'unità più forte in campo, potenzia di 3 un alleato, altrimenti lo potenzia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se controlli l'unità più forte in campo, potenzia di 3 un alleato, altrimenti lo potenzia di 1.\n"},200028:{name:"Vabjorn",flavor:"Sangue per Svalblod! Teschi per il suo trono!",info:"Schieramento, Prima fila: distrugge un nemico danneggiato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un nemico danneggiato.\n"},200030:{name:"Milaen",flavor:"Il vecchio barone non avrebbe avuto pietà per i bracconieri. Per fortuna di Milaen, il vecchio barone era morto e i suoi uomini erano diventati dei fuorilegge.",info:"Schieramento, Prima fila: danneggia di 4 un nemico.\nSchieramento, Retrovie: danneggia 4 nemici di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 4 un nemico.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: danneggia 4 nemici di 1.\n"},200031:{name:"Leo Bonhart",flavor:"Si limitava a stare seduto e a guardarmi senza dire una parola. Aveva certi occhi... un po' come quelli dei pesci. Senza sopracciglia, senza ciglia... Due piccoli globi acquosi, in ciascuno dei quali era sospeso un nocciolo nero. Mi fissava con quegli occhi e taceva. Mi ha spaventato più così che con le botte.",info:"Schieramento, Prima fila: distrugge un nemico con almeno 8 di forza.\nSchieramento, Retrovie: distrugge un Witcher.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un nemico con almeno 8 di forza.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: distrugge un Witcher.\n"},200032:{name:"Rainfarn di Attre",flavor:"Dopo la caduta di Cintra, fu la volta di Attre, lasciando ai suoi difensori una scelta: accettare il giogo nilfgaardiano o morire.",info:"Schieramento: potenzia di 3 un nemico, poi si potenzia di 3.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 3 un nemico, poi si potenzia di 3.\n"},200033:{name:"Adepta di Aretuza",flavor:"Le adepte conducono una vita privilegiata ad Aretuza, dove possono soddisfare ogni loro capriccio. D'altro canto, mezza città si guadagna da vivere grazie alla loro presenza: sarte, commercianti, pasticcieri, fattorini…",info:"Fervore.\nOrdine, Retrovie: aggiunge 1 alle Cariche di un'unità alleata.\nRecupero: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: aggiunge 1 alle <keyword=charge>Cariche</keyword> di un'unità alleata.\n<keyword=cooldown>Recupero</keyword>: 2.\n"},200036:{name:"Drummond: guerraiolo",flavor:"Il bene superiore? La guerra è il bene superiore, il male superiore e l'invenzione superiore dell'uomo!",info:"Schieramento, Prima fila, Sete di sangue 1: distrugge un manufatto nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>, <keyword=bloodthirst>Sete di sangue</keyword> 1: distrugge un manufatto nemico.\n"},200037:{name:"Ciclope",flavor:"Non guardarlo nell'occhio. Lo detesta...",info:"Schieramento: distrugge un'unità alleata adiacente e danneggia un nemico di un valore pari alla forza dell'unità distrutta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un'unità alleata adiacente e danneggia un nemico di un valore pari alla forza dell'unità distrutta.\n"},200038:{name:"Archeospora",flavor:"La tradizione popolare vuole che le archeospore nascano dal suolo bagnato dal sangue dei morti. Per questo motivo, crescono in terreni consacrati da massacri, rituali oscuri o brutali omicidi.",info:"Prosperità.\nSchieramento: danneggia di 2 un nemico casuale. \nUltimo desiderio: ripete l'abilità Schieramento.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>.\n<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico casuale. \n<keyword=deathwish>Ultimo desiderio</keyword>: ripete l'abilità <keyword=deploy>Schieramento</keyword>.\n"},200039:{name:"Dol Blathanna: difesa",flavor:"Finché avremo vita, nessun umano potrà calpestare i verdi pascoli di Dol Blathanna.",info:"Prima fila: ogni volta che un nemico viene cambiato di fila, lo danneggia di 1.\nRetrovie: ogni volta che un alleato viene cambiato di fila, lo potenzia di 1.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: ogni volta che un nemico viene cambiato di fila, lo danneggia di 1.\n<keyword=ranged>Retrovie</keyword>: ogni volta che un alleato viene cambiato di fila, lo potenzia di 1.\n"},200040:{name:"An Craite: spadone",flavor:"Ah, mi fai proprio ridere, uomo del Nord! Guardatelo, il poppante non riesce neanche a impugnarlo, figuriamoci riuscire a sventrarmi.",info:"Ogni volta che un nemico della fila opposta viene danneggiato, si potenzia di 1.\n",infoRaw:"Ogni volta che un nemico della fila opposta viene danneggiato, si potenzia di 1.\n"},200041:{name:"Grande Helge",flavor:"Non è l'ideale per conquistare una città, ma è perfetto per distruggerla.",info:"Immune.\nOrdine: danneggia di 2 un nemico per ogni Soldato presente su questa fila.\n",infoRaw:"<keyword=immune>Immune</keyword>.\n<keyword=order>Ordine</keyword>: danneggia di 2 un nemico per ogni Soldato presente su questa fila.\n"},200042:{name:"Brigante di Mahakam",flavor:"Andare a caccia sulle colline scoscese di Mahakam non è particolarmente facile... D'altro canto, il pericolo non hai mai spaventato i nani.",info:"Schieramento: se questa unità è potenziata, ottiene Resistenza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se questa unità è potenziata, ottiene <keyword=resilient>Resistenza</keyword>.\n"},200043:{name:"Facciadisegugio",flavor:"Un tempo fiero jarl dei Tordarroch, ora non è altro che un povero pazzo.",info:"Schieramento: evoca 3 Amici di Harald e li richiama sull'altra fila nella tua metà di campo.\nOrdine: danneggia di 1 un alleato.\nRecupero: 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> 3 Amici di Harald e li <keyword=summon>richiama</keyword> sull'altra fila nella tua metà di campo.\n<keyword=order>Ordine</keyword>: danneggia di 1 un alleato.\n<keyword=cooldown>Recupero</keyword>: 1.\n"},200044:{name:"Divisione Magne",flavor:"Capisco che l'imperatore voglia difendere tutte le sue terre, ma... una guarnigione in questa landa devastata è come una cintura di castità indosso a una strega dei sepolcri.",info:"Alla fine del turno, si potenzia di 1 se è l'unica carta di questa fila.\n",infoRaw:"Alla fine del turno, si potenzia di 1 se è l'unica carta di questa fila.\n"},200046:{name:"Tuirseach: veterano",flavor:"Ai miei tempi ho visto meraviglie che chiunque altro poteva solo sognare, e fatto cose che nessun uomo dovrebbe mai fare.",info:"Schieramento: si danneggia di 4.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si danneggia di 4.\n"},200048:{name:"Tutore di Ban Ard",flavor:"Confronto spesso i risultati delle adepte di Aretuza con il lavoro svolto dagli studenti di Ban Ard. Conclusione: le giovani sono molto più in gamba.",info:"Schieramento: potenzia di 1 un alleato per ognuna delle relative Cariche.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 un alleato per ognuna delle relative <keyword=charge>Cariche</keyword>.\n"},200049:{name:"Ariete",flavor:"Non vogliono aprire, eh? Bene, busseremo più forte.",info:"Schieramento: distrugge un manufatto nemico se controlli un Soldato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico se controlli un Soldato.\n"},200050:{name:"Vreemde",flavor:"Il Grande Sole scalderà il Nord con i suoi raggi, a costo di dovervi incenerire tutti!",info:"Schieramento: potenzia di 2 un Soldato alleato e tutte le relative copie.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un Soldato alleato e tutte le relative copie.\n"},200052:{name:"Golyat",flavor:"Si narra che un tempo Golyat fosse un famoso cavaliere. Purtroppo per lui, un giorno suscitò l'ira della Signora del Lago, che lo tramutò in un mostro.",info:"Ultimo desiderio: l'avversario richiama l'unità meno forte del suo mazzo sulla fila opposta.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: l'avversario <keyword=summon>richiama</keyword> l'unità meno forte del suo mazzo sulla fila opposta.\n"},200053:{name:"Compressione",flavor:"La statuetta si espanse in un lampo, pulsando e vibrando, mutando forma e consistenza come uno sbuffo di fumo strisciante. Fasci di luce rivelarono un movimento e l'indurimento dei materiali. Un attimo dopo, al centro del cerchio magico, comparve una figura umana.",info:"Blocca un'unità e la riporta alla sua forza base.\n",infoRaw:"<keyword=lock>Blocca</keyword> un'unità e la riporta alla sua forza base.\n"},200055:{name:"L'Invisibile",flavor:"Nessuno sa esattamente quanto sia antico l'Invisibile, neanche i vampiri superiori. Sanno solo che non devono mai osare opporsi al suo volere, per nessun motivo.",info:"Ordine: attiva l'abilità Ultimo desiderio di un alleato.\nAll'inizio del round, ripristina questa abilità.\n",infoRaw:"<keyword=order>Ordine</keyword>: attiva l'abilità <keyword=deathwish>Ultimo desiderio</keyword> di un alleato.\nAll'inizio del round, ripristina questa abilità.\n"},200056:{name:"Aguara: originale",flavor:"Mai sentito parlare di un anterion? In pratica, è l'opposto di un licantropo: è una bestia che può assumere forma umana.",info:"Schieramento, Retrovie: crea e gioca una carta Speciale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=create>crea</keyword> e gioca una carta Speciale.\n"},200058:{name:"Maledizione di Uma",flavor:"Ti ho dato tre tracce da seguire, fresche come la rugiada. Ti ho offerto l'aiuto delle mie spie e della mia maga di corte. E al posto di mia figlia tu mi porti questa... mostruosità?",info:"Crea e metti in gioco un'unità Oro.\n",infoRaw:"<keyword=create>Crea</keyword> e metti in gioco un'unità Oro.\n"},200062:{name:"Aguara",flavor:"Comportati bene, o l'aguara ti porta via!",info:"Schieramento, Prima fila: blocca un'unità.\nSchieramento, Retrovie: rimuove il blocco di un'unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=lock>blocca</keyword> un'unità.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: rimuove il <keyword=lock>blocco</keyword> di un'unità.\n"},200071:{name:"Shilard Fitz-Oesterlen",flavor:"Il diplomatico è un uomo che usa più parole del dovuto per esprimere meno di quanto sappia.",info:"Schieramento: imposta a 1 la forza dell'unità più forte in mano all'avversario.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: imposta a 1 la forza dell'unità più forte in mano all'avversario.\n"},200073:{name:"Adda: Striga",flavor:"La striga? Tutto a posto. Certo, ogni tanto sbrana qualcuno, ma niente a cui non ci si possa abituare.",info:"Schieramento: se controlli l'unità più forte, danneggia di 5 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se controlli l'unità più forte, danneggia di 5 un nemico.\n"},200078:{name:"Prova delle Erbe",flavor:"Immaginate un blocco d'argilla. Prima di dargli forma bisogna ammorbidirlo, altrimenti si rompe. La prova serve a questo: prepara il corpo al cambiamento, per così dire. Del resto è così che le sostanze mutagene creano i witcher.",info:"Potenzia un'unità fino a 12 di forza. Se non è un Witcher, prima la danneggia di 6.\n",infoRaw:"Potenzia un'unità fino a 12 di forza. Se non è un Witcher, prima la danneggia di 6.\n"},200079:{name:"Sogno blu di Hanmarvyn",flavor:"Questo incantesimo permette di vedere gli ultimi attimi di vita di un defunto... a patto di sopravvivere al suo impeto magico.",info:"Metti in gioco un'unità dal cimitero dell'avversario e assegnale l'attributo Condannata.\n",infoRaw:"Metti in gioco un'unità dal cimitero dell'avversario e assegnale l'attributo <keyword=doomed>Condannata</keyword>.\n"},200080:{name:"Xavier Moran",flavor:"Beh? Qualcosa non va, principessina? Il sapore della cacciagione è troppo forte per te?",info:"Prima fila: ogni volta che questa unità viene potenziata, si potenzia ulteriormente di 2.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: ogni volta che questa unità viene potenziata, si potenzia ulteriormente di 2.\n"},200081:{name:"Heymaey: erborista",flavor:"A Skellige, noi non bruciamo le donne più intelligenti, ma accettiamo i loro consigli.",info:"Schieramento, Retrovie: cura di 2 un alleato, poi lo potenzia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=heal>cura</keyword> di 2 un alleato, poi lo potenzia di 2.\n"},200083:{name:"Compagni di Iris",flavor:"Preferiremmo tenere per noi i nostri nomi. Vedici come... amici della casa.",info:"Schieramento: pesca a una carta a tua scelta dal tuo mazzo, poi scarta una carta casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: pesca a una carta a tua scelta dal tuo mazzo, poi <keyword=discard>scarta</keyword> una carta casuale.\n"},200087:{name:"Dorregaray di Vole",flavor:"Come i witcher, Dorregaray è un esperto di mostri e ha sviluppato un peculiare sistema di classificazione. Invece di suddividerli in necrofagi, ogroidi e compagnia bella, li considera tutti bellissimi. Punto e basta.",info:"Schieramento: blocca un'unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=lock>blocca</keyword> un'unità.\n"},200088:{name:"Hubert Rejk",flavor:"Il medico legale è una persona calma e pacifica. Tratta perfino i cadaveri con assoluta delicatezza.",info:"Schieramento: si potenzia di un valore pari ai danni che hai inflitto ai nemici durante il turno.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di un valore pari ai danni che hai inflitto ai nemici durante il turno.\n"},200089:{name:"Nivellen",flavor:"Ti sei perso? Vedi di perderti altrove, invece che nella mia proprietà. Punta l'orecchio sinistro verso il sole e cammina. Prima o poi troverai la strada. Allora? Cosa aspetti?",info:"Schieramento: sposta tutte le unità da una fila all'altra della stessa metà di campo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: sposta tutte le unità da una fila all'altra della stessa metà di campo.\n"},200091:{name:"Stregobor",flavor:"Il witcher, nella sua carriera, si era imbattuto in ladri che sembravano dignitari, dignitari che sembravano accattoni e re che sembravano ladri. Stregobor, tuttavia, era esattamente quello sembrava: l'archetipo del mago.",info:"Retrovie: dopo 2 turni, entrambi i giocatori pescano un'unità e ne impostano la forza a 1.\n",infoRaw:"<keyword=ranged>Retrovie</keyword>: dopo 2 turni, entrambi i giocatori pescano un'unità e ne impostano la forza a 1.\n"},200098:{name:"Vincent Meis",flavor:"Di giorno, capitano della guardia cittadina di Vizima. Di notte, spietato vendicatore e difensore degli oppressi.",info:"Fervore.\nOrdine, Prima fila: imposta a 1 la forza di un nemico non potenziato.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: imposta a 1 la forza di un nemico non potenziato.\n"},200102:{name:"Hym",flavor:"Gli dei mi parlano... li sento bisbigliare dalle ombre...",info:"Schieramento: si scambia di forza con un'unità danneggiata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si scambia di forza con un'unità danneggiata.\n"},200103:{name:"Olaf",flavor:"Il dieci volte vincitore del venerabile titolo di Campione dell'Arena di An Skellig.",info:"Ordine: potenzia Olaf di una quantità doppia rispetto a quanto è danneggiato.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia Olaf di una quantità doppia rispetto a quanto è danneggiato.\n"},200104:{name:"Ulfhedinn",flavor:"Un lupo mannaro? Magari! Questo è molto, molto peggio.",info:"Schieramento: danneggia un nemico di un valore pari alla metà della sua forza attuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia un nemico di un valore pari alla metà della sua forza attuale.\n"},200105:{name:"Dimun: nave da guerra",flavor:"Le navi da guerra dei Dimun sono leggere e veloci, ideali per inseguire i lenti mercantili.",info:"Schieramento: per tre volte, danneggia di 1 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: per tre volte, danneggia di 1 un nemico.\n"},200112:{name:"Sirena",flavor:"Secondo la leggenda, le sirene adescano i marinai con il loro suadente canto. In realtà, è più probabile che i lupi di mare siano attratti da ben altre loro generose prosperità...",info:"Schieramento: potenzia di 3 un alleato con Ultimo desiderio.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 3 un alleato con <keyword=deathwish>Ultimo desiderio</keyword>.\n"},200114:{name:"Lupo mannaro alfa",flavor:"Alcuni dicono che la licantropia sia contagiosa: se un lupo mannaro ti morde, diventi come lui. I witcher sanno bene che è una mera superstizione: solo un potente maleficio può causare questa condizione.",info:"Immune, Prosperità.\n",infoRaw:"<keyword=immune>Immune</keyword>, <keyword=thrive>Prosperità</keyword>.\n"},200115:{name:"Assassinio",flavor:"L'assassino è una tra le professioni più popolari nelle corti imperiali, subito dopo lo scriba e la cortigiana.",info:"Danneggia di 6 un'unità. Riduce i danni di 1 per ogni unità adiacente a essa.\n",infoRaw:"Danneggia di 6 un'unità. Riduce i danni di 1 per ogni unità adiacente a essa.\n"},200118:{name:"Infiltrato",flavor:"Scappa pure, non riuscirai a nasconderti.",info:"Prima fila: alla fine del round, rimescola questa unità nel mazzo avversario.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: alla fine del round, rimescola questa unità nel mazzo avversario.\n"},200124:{name:"Witcher della Vipera",flavor:"La Scuola della Vipera rinascerà. Grazie a Letho.",info:"Schieramento: svela la prima carta del mazzo avversario e la bandisce.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=reveal>svela</keyword> la prima carta del mazzo avversario e la <keyword=banish>bandisce</keyword>.\n"},200135:{name:"Pirotecnico",flavor:"Una professione molto rischiosa, ma anche molto rispettata, a Mahakam. Il suo più noto esponente è stato un nano di nome Mikkel Bay.",info:"Fervore.\nOrdine: distrugge un manufatto alleato e danneggia di 2 un'unità. \nRecupero: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: distrugge un manufatto alleato e danneggia di 2 un'unità. \n<keyword=cooldown>Recupero</keyword>: 2.\n"},200136:{name:"Alta profetessa",flavor:"A volte le Sue parole risultano incomprensibili, ma contengono sempre significati nascosti e tanta, tanta saggezza.",info:"Schieramento, Retrovie: svela una carta casuale del tuo mazzo. Se è una carta Speciale, questa unità si potenzia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=reveal>svela</keyword> una carta casuale del tuo mazzo. Se è una carta Speciale, questa unità si potenzia di 2.\n"},200138:{name:"Saggio",flavor:"La conoscenza, mia cara, è un privilegio. Come tale, va condiviso solo tra individui di uguale levatura.",info:"Si potenzia di 2 ogni volta che giochi una carta Speciale.\n",infoRaw:"Si potenzia di 2 ogni volta che giochi una carta Speciale.\n"},200139:{name:"Pantera",flavor:"Una volta, uno studioso di Oxenfurt esaminò una pantera, e sentenziò che non era altro che un leopardo di colore diverso. La pantera sembrò indifferente a tale conclusione, e se lo divorò in un sol boccone prima che potesse completare la sua ricerca.",info:"Schieramento: danneggia di 3 un'unità nemica non appartenente agli Scoia'tael.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 3 un'unità nemica non appartenente agli Scoia'tael.\n"},200144:{name:"Tuirseach: domatore",flavor:"Non toccarlo. Non guardarlo negli occhi. Anzi... è meglio se non ti avvicini proprio.",info:"Schieramento: potenzia di 2 una Bestia alleata. Aumenta di 1 il potenziamento per ogni Bestia presente nel tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 una Bestia alleata. Aumenta di 1 il potenziamento per ogni Bestia presente nel tuo cimitero.\n"},200145:{name:"Dimun: corsaro",flavor:"Il mare è nostro, insieme a tutto quello che vi galleggia, nuota o veleggia!",info:"Schieramento: danneggia di 1 un'unità per ogni Nave presente nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un'unità per ogni Nave presente nella tua mano.\n"},200146:{name:"Dimun: affarista",flavor:"Spikeroog è un posto desolato, ma la cosa non ci preoccupa. Quello che ci serve lo prendiamo da te.",info:"Schieramento, Prima fila: danneggia di 1 un'unità, poi la potenzia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 1 un'unità, poi la potenzia di 2.\n"},200147:{name:"Heymaey: flaminica",flavor:"La flaminica, che rappresenta il rango più alto tra le druide, è tenuta in grande considerazione ed è molto, molto potente.",info:"Alla fine di ogni turno, cura di 1 tutti gli alleati danneggiati di questa fila.\n",infoRaw:"Alla fine di ogni turno, <keyword=heal>cura</keyword> di 1 tutti gli alleati danneggiati di questa fila.\n"},200149:{name:"Heymaey: protettore",flavor:"Non teme nulla, eccetto l'ira di Freya... e di sua moglie.",info:"Si potenzia di 1 ogni volta che un'unità adiacente viene danneggiata.\n",infoRaw:"Si potenzia di 1 ogni volta che un'unità adiacente viene danneggiata.\n"},200154:{name:"Decreto reale",flavor:"Io, Foltest, Re di Temeria per diritto divino, Principe di Sodden, Gran Protettore di Brugge, eccetera, eccetera... decreto quanto segue...",info:"Metti in gioco un'unità dal tuo mazzo.\n",infoRaw:"Metti in gioco un'unità dal tuo mazzo.\n"},200159:{name:"Bran Tuirseach",flavor:"Nessuno potrà sostituire Bran. Anche se in molti ci proveranno.",info:"Ordine: scarta una carta, poi pesca una carta.\nAll'inizio del round, ripristina questa abilità.\nOgni volta che scarti una carta, danneggia di 1 un nemico casuale.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=discard>scarta</keyword> una carta, poi pesca una carta.\nAll'inizio del round, ripristina questa abilità.\nOgni volta che <keyword=discard>scarti</keyword> una carta, danneggia di 1 un nemico casuale.\n"},200160:{name:"Crach an Craite",flavor:"I nilfgaardiani lo chiamano Tirth ys Muire, il Cinghiale del Mare. Lo nominano per spaventare i bambini!",info:"Ordine: danneggia di 1 un'unità. \nRecupero: 2.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=cooldown>Recupero</keyword>: 2.\n"},200161:{name:"Harald lo Zoppo",flavor:"Nessuno sa da cosa derivi il soprannome. E nessuno ha mai osato chiedere.",info:"Ordine: danneggia casualmente tutti i nemici per un valore complessivo di 8.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia casualmente tutti i nemici per un valore complessivo di 8.\n"},200162:{name:"Emhyr var Emreis",flavor:"Il Paziente non rientra tra i miei nomi. Spera che non ti chiamino Il Senzatesta.",info:"Ordine: sposta un'unità o un manufatto alleato dal campo alla tua mano, poi gioca una carta.\n",infoRaw:"<keyword=order>Ordine</keyword>: sposta un'unità o un manufatto alleato dal campo alla tua mano, poi gioca una carta.\n"},200163:{name:"Morvran Voorhis",flavor:"Il sole estivo che si riflette nelle tranquille acque dell'Alba: ecco cos'è Nilfgaard per me.",info:"Ordine: svela una carta casuale presente nel mazzo dell'avversario e potenzia di 2 un alleato. \nCariche: 3.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=reveal>svela</keyword> una carta casuale presente nel mazzo dell'avversario e potenzia di 2 un alleato. \n<keyword=charge>Cariche</keyword>: 3.\n"},200164:{name:"Jan Calveit",flavor:"La spada è solo uno dei tanti strumenti nelle mani di un sovrano.",info:"Ordine: guarda le prime 3 carte del tuo mazzo e giocane una.\n",infoRaw:"<keyword=order>Ordine</keyword>: guarda le prime 3 carte del tuo mazzo e giocane una.\n"},200165:{name:"Francesca Findabair",flavor:"Per vivere in pace, prima bisogna uccidere. È il destino ultimo dell'oppressione umana.",info:"Ordine: gioca una carta Speciale dal tuo cimitero.\n",infoRaw:"<keyword=order>Ordine</keyword>: gioca una carta Speciale dal tuo cimitero.\n"},200166:{name:"Eithné",flavor:"La regina delle driadi ha gli occhi di argento fuso e un cuore d'acciaio.",info:"Ordine: danneggia di 1 un'unità. \nCariche: 4.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 4.\n"},200167:{name:"Brouver Hoog",flavor:"Quel vecchio barbagianni? Non si capisce nemmeno se è vivo o impagliato!",info:"Ordine: sposta un'unità sull'altra sua fila. Se è un nemico, lo danneggia di 2. Se è un alleato, lo potenzia di 2. \nCariche: 3.\n",infoRaw:"<keyword=order>Ordine</keyword>: sposta un'unità sull'altra sua fila. Se è un nemico, lo danneggia di 2. Se è un alleato, lo potenzia di 2. \n<keyword=charge>Cariche</keyword>: 3.\n"},200168:{name:"Re Foltest",flavor:"Altro che i consiglieri e i loro intrighi! Mi fido più della spada dei miei soldati.",info:"Ordine: potenzia di 1 un alleato e gli fornisce Fervore.\nCariche: 3.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 1 un alleato e gli fornisce <keyword=zeal>Fervore</keyword>.\n<keyword=charge>Cariche</keyword>: 3.\n"},200170:{name:"Re Henselt",flavor:"Re Henselt non aveva certo l'aspetto di un ladro, ma, con il dovuto rispetto, era proprio un mascalzone.",info:"Ordine: mette in campo un'unità alleata dal tuo mazzo, la potenzia di 4 e le fornisce Fervore.\n",infoRaw:"<keyword=order>Ordine</keyword>: mette in campo un'unità alleata dal tuo mazzo, la potenzia di 4 e le fornisce <keyword=zeal>Fervore</keyword>.\n"},200171:{name:"Tridam: fanteria",flavor:"I soldati leali al vecchio barone di Tridam sono fuggiti dalla città con Falibor, diventando così dei ricercati.",info:"Ogni volta che questa unità viene potenziata, danneggia di 1 un nemico casuale.\n",infoRaw:"Ogni volta che questa unità viene potenziata, danneggia di 1 un nemico casuale.\n"},200177:{name:"Cerys an Craite",flavor:"Sai perché mi chiamano Aquila? Perché i topolini come te li mangio a colazione.",info:"Se questa unità è nel cimitero, viene richiamata su una fila alleata casuale ogni volta che metti in gioco un'unità dal tuo cimitero.\n",infoRaw:"Se questa unità è nel cimitero, viene <keyword=summon>richiamata</keyword> su una fila alleata casuale ogni volta che metti in gioco un'unità dal tuo cimitero.\n"},200209:{name:"Saskia",flavor:"Non m'importa dei re e dei loro titoli. A est vive la persona che davvero merita una corona.",info:"Ordine: danneggia di 2 un nemico.\nRecupero: 4.\n Se in mano hai un Elfo, un Nano o una Driade, riduce il Recupero di 1 (una sola volta per categoria).\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 2 un nemico.\n<keyword=cooldown>Recupero</keyword>: 4.\n Se in mano hai un Elfo, un Nano o una Driade, riduce il <keyword=cooldown>Recupero</keyword> di 1 (una sola volta per categoria).\n"},200212:{name:"Skjall",flavor:"Cancellate il suo nome dalla Saga degli Anziani! Che nessuno osi offrirgli rifugio o appoggio!",info:"Schieramento: danneggia di 4 un nemico non danneggiato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico non danneggiato.\n"},200218:{name:"Jotunn",flavor:"Secondo le leggende di Skellige, in passato le isole erano governate dal possente e temibile Jotunn, re dei Giganti. Venne ucciso da Hemdall, ma morendo promise che sarebbe tornato per il Ragh nar Roog.",info:"Schieramento, Prima fila: si potenzia di 2 per ogni unità in più che ha in campo il tuo avversario rispetto a te.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: si potenzia di 2 per ogni unità in più che ha in campo il tuo avversario rispetto a te.\n"},200220:{name:"Whispess: tributo",flavor:"Sarei la donna migliore della tua vita... e l'ultima!",info:"Schieramento, Retrovie: gioca una carta Organismo dal mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: gioca una carta Organismo dal mazzo.\n"},200221:{name:"Sacrificio rituale",flavor:"Ti faremo a pezzi, ragazzo. Faremo un brodo con la tua carne!",info:"Attiva tutte le abilità Ultimo desiderio degli alleati.\n",infoRaw:"Attiva tutte le abilità <keyword=deathwish>Ultimo desiderio</keyword> degli alleati.\n"},200222:{name:"Weavess: sortilegio",flavor:"Sento il tuo dolore. Vedo la tua paura!",info:"Schieramento, Prima fila: consuma un'unità nella tua mano, poi pesca una carta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=consume>consuma</keyword> un'unità nella tua mano, poi pesca una carta.\n"},200223:{name:"Mandragora",flavor:"Tutti compagni! Si stupiscono, non vogliono credere alla grande scoperta! L'uno vaneggia cianciando di mandragore, l'altro vantando un cane nero.",info:"Ripristina un'unità.\n",infoRaw:"<keyword=reset>Ripristina</keyword> un'unità.\n"},200224:{name:"Occhio di corvo",flavor:"Un famoso pirata amava così tanto quest'erba inebriante che ottenne il soprannome di Occhio di corvo. L'adattamento teatrale della sua figura leggendaria, tuttavia, omette questo particolare... con grande rammarico degli appassionati di pirateria.",info:"Attiva l'abilità Ultimo desiderio di un'unità Bronzo.\n",infoRaw:"Attiva l'abilità <keyword=deathwish>Ultimo desiderio</keyword> di un'unità Bronzo.\n"},200225:{name:"Museruola di Vigo",flavor:"Non esiste bestia che Fringilla Vigo non riesca a domare.",info:"Sequestra un nemico con al massimo 5 di forza e lo blocca.\n",infoRaw:"<keyword=seize>Sequestra</keyword> un nemico con al massimo 5 di forza e lo <keyword=lock>blocca</keyword>.\n"},200226:{name:"Aconito",flavor:"Noto anche come re dei veleni, l'aconito è usato in diverse pozioni e composti alchemici dei witcher.",info:"Danneggia di 2 tutte le unità con forza pari oppure danneggia di 2 tutte le unità con forza dispari.\n",infoRaw:"Danneggia di 2 tutte le unità con forza pari oppure danneggia di 2 tutte le unità con forza dispari.\n"},200228:{name:"Fossa letale",flavor:"Subdola, facile da realizzare e dall'efficacia letale. Non per niente è la trappola preferita dagli Scoia'tael.",info:"Imboscata: distrugge la prossima unità messa in gioco dall'avversario prima che se ne attivino le abilità.\n",infoRaw:"<keyword=ambush>Imboscata</keyword>: distrugge la prossima unità messa in gioco dall'avversario prima che se ne attivino le abilità.\n"},200233:{name:"Cockatrice",flavor:"Colpisce sempre tra le vertebre, sotto al rene sinistro o dritto all'aorta. In questo modo, non ha la necessità di colpire una seconda volta.",info:"Schieramento: per ogni Bestia adiacente che controlli, danneggia di 2 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: per ogni Bestia adiacente che controlli, danneggia di 2 un nemico.\n"},200234:{name:"Povera Fanteria Fottuta",flavor:"Sono un veterano di guerra! Hai una corona?",info:"Schieramento: si potenzia di 4.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 4.\n"},200235:{name:"Lambert: spadaccino",flavor:"Va' a insegnare a tua nonna come si succhiano le uova.",info:"Schieramento: distrugge un nemico con Resistenza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un nemico con <keyword=resilient>Resistenza</keyword>.\n"},200236:{name:"Eskel: esploratore",flavor:"Ti sentivo ansimare da tre miglia di distanza, semplicemente non volevo rinunciare alla mia posizione avvantaggiata.",info:"Prima fila: alla fine di ogni turno, si potenzia di 1.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: alla fine di ogni turno, si potenzia di 1.\n"},200237:{name:"Vesemir: mentore",flavor:"Uccidere mostri è una faccenda da non prendere alla leggera. Ciri deve capirlo, se vuole diventare una di noi.",info:"Schieramento: potenzia di 1 tutti i Witcher alleati presenti in campo, in mano o nel mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 1 tutti i Witcher alleati presenti in campo, in mano o nel mazzo.\n"},200293:{name:"Sobillatore nanico",flavor:"Ricordati le mie parole: se non alziamo tutti le chiappe, gli umani verranno a rapire le nostre donne!",info:"Schieramento, Retrovie: potenzia di 2 un Nano nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 2 un Nano nella tua mano.\n"},200294:{name:"Portabandiera",flavor:"Difendete questo stendardo a costo della vita! Gloir aen Ard Feain!",info:"Schieramento: potenzia di 2 un alleato.\nOgni volta che sveli questa carta, potenzia di 2 un alleato casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un alleato.\nOgni volta che <keyword=reveal>sveli</keyword> questa carta, potenzia di 2 un alleato casuale.\n"},200295:{name:"Demone putrefatto",flavor:"Prima ne senti il lezzo, poi ne vedi la sagoma.",info:"Ultimo desiderio: danneggia di 4 un nemico casuale.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: danneggia di 4 un nemico casuale.\n"},200296:{name:"Cavalleria Alba",flavor:"I cavalieri, disposti a cuneo, si aprirono una breccia nella falange con fragore. Come un pugnale che si fa strada nelle viscere, la divisione Alba raggiunse il cuore della fanteria temeriana senza fatica alcuna.",info:"Schieramento, Prima fila: blocca un'unità nemica.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=lock>blocca</keyword> un'unità nemica.\n"},200299:{name:"Temeria: tamburino",flavor:"Non era la carriera che sognava quando diceva alla madre che voleva fare il musicista.",info:"Ordine: potenzia di 1 un alleato e gli fornisce Fervore.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 1 un alleato e gli fornisce <keyword=zeal>Fervore</keyword>.\n"},200300:{name:"Preda da trofeo",flavor:"Gli abitanti di Skellige sanno bene che gli arpioni non sono efficaci solo con le balene...",info:"Sposta un nemico sull'altra sua fila e lo danneggia di un valore pari al numero di unità su quella fila.\n",infoRaw:"Sposta un nemico sull'altra sua fila e lo danneggia di un valore pari al numero di unità su quella fila.\n"},200301:{name:"Naglfar",flavor:"Le onde si infrangono sulla riva, sospinte dalla prora della spettrale Naglfar. Il corno di Hemdall risuona nell'aria mentre l'eroe si erge sopra ai nemici dal Bifrost, il ponte dell'arcobaleno. Il Bianco Gelo si avvicina, portatore di vento e tempesta...",info:"Guarda 2 carte Oro casuali del tuo mazzo, poi giocane una e sposta l'altra in cima al mazzo.\n",infoRaw:"Guarda 2 carte Oro casuali del tuo mazzo, poi giocane una e sposta l'altra in cima al mazzo.\n"},200320:{name:"Chort",flavor:"Membro della Forza di difesa bovina. Eterna fedeltà!",info:"Nessuna abilità.\n",infoRaw:"Nessuna abilità.\n"},200502:{name:"Troll dei ghiacci",flavor:"I troll si distinguono per forma, dimensioni e inclinazioni. Una cosa, tuttavia, li accomuna: hanno tanto cervello quanto un secchio di chiodi arrugginiti.",info:"Alla fine di ogni turno, se controlli meno unità in campo rispetto al tuo avversario, si potenzia di 1.\n",infoRaw:"Alla fine di ogni turno, se controlli meno unità in campo rispetto al tuo avversario, si potenzia di 1.\n"},200518:{name:"Venendal: unità élite",flavor:"La regione di Ebbing è famosa per gli abili mercenari e la cavalleria leggera.",info:"Schieramento: se in questo turno hai usato l'abilità del tuo comandante, danneggia di 3 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se in questo turno hai usato l'abilità del tuo comandante, danneggia di 3 un nemico.\n"},200519:{name:"Birra di Mahakam",flavor:"Ritenuto il più importante contributo dei nani alla cultura mondiale.",info:"Potenzia di 4 un'unità e ne rimuove il blocco.\n",infoRaw:"Potenzia di 4 un'unità e ne rimuove il <keyword=lock>blocco</keyword>.\n"},200520:{name:"Éibhear Hattori",flavor:"I suoi ravioli sono l'unica cosa che potrebbe rivaleggiare con le sue spade.",info:"Ordine: metti in gioco un manufatto Bronzo dal tuo cimitero.\n",infoRaw:"<keyword=order>Ordine</keyword>: metti in gioco un manufatto Bronzo dal tuo cimitero.\n"},200523:{name:"Germain Piquant",flavor:"L'eroe di cui Toussaint ha bisogno, ma non quello che si merita.",info:"Schieramento, Prima fila: evoca 2 Mucche a destra e a sinistra di questa unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=spawn>evoca</keyword> 2 Mucche a destra e a sinistra di questa unità.\n"},200528:{name:"Heymaey: lanciera",flavor:"Le donne di Skellige sono selvagge e imprevedibili, e rappresentano una minaccia concreta per l'Impero. Mai e poi mai sottovalutare le loro capacità. - Direttiva rivolta alle forze della flotta imperiale",info:"Schieramento: danneggia di 3 un nemico, poi si danneggia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 3 un nemico, poi si danneggia di 2.\n"},200529:{name:"Ronvid il Petulante",flavor:"Autoinvestitosi cavaliere, Ronvid gira il mondo per difendere l'onore dell'amata damigella Bilberry, che tuttavia nessuno ha mai visto o, ammesso che esista, sa se sia ancora viva.",info:"Schieramento: si potenzia di 5.\nOgni volta che metti in gioco un Soldato, richiama questa unità dal tuo cimitero su una fila alleata casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 5.\nOgni volta che metti in gioco un Soldato, <keyword=summon>richiama</keyword> questa unità dal tuo cimitero su una fila alleata casuale.\n"},200530:{name:"Birra contaminata",flavor:"Ma è normale che quella birra sia verde...?",info:"Fervore.\nOrdine: danneggia di 2 un'unità. \nCariche: 3.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: danneggia di 2 un'unità. \n<keyword=charge>Cariche</keyword>: 3.\n"},200532:{name:"Birra degli antenati",flavor:"Boros, il leggendario fondatore del clan dei Fuchs, passò all'altro mondo dopo aver esagerato con questo prelibato nettare. Morì affogato in un ruscello, dopo esservi svenuto dentro mentre cercava il suo anello d'oro che vi era caduto.",info:"Fervore.\nOrdine: potenzia di 3 un'unità. \nRecupero: 2.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: potenzia di 3 un'unità. \n<keyword=cooldown>Recupero</keyword>: 2.\n"},200534:{name:"Trollessa di Vergen",flavor:"Amante della zuppa d'elfi e cipolle.",info:"Prima fila: si potenzia di 2 ogni volta che un alleato viene distrutto durante il tuo turno.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: si potenzia di 2 ogni volta che un alleato viene distrutto durante il tuo turno.\n"},200535:{name:"Spadaccino elfico",flavor:"Combattere è una danza, e non devi mai concedere all'avversario di condurla.",info:"Ordine, Prima fila: danneggia di 1 un nemico.\nRecupero: 2.\nOgni volta che metti in gioco un Elfo, riduce il Recupero di 1.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 1 un nemico.\n<keyword=cooldown>Recupero</keyword>: 2.\nOgni volta che metti in gioco un Elfo, riduce il <keyword=cooldown>Recupero</keyword> di 1.\n"},200539:{name:"Scaltrocertola",flavor:"Nessun uomo, neppure il più forte o il più abile, può parare il colpo inferto dalla coda di una scaltrocertola, dalle chele di uno scorpione gigante o dagli artigli di un grifone.",info:"Ordine: consuma un'unità alleata di questa fila.\nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=consume>consuma</keyword> un'unità alleata di questa fila.\n<keyword=cooldown>Recupero</keyword>: 1.\n"},200540:{name:"Aedirn: demolitore",flavor:"Ugh, questi tizi fanno venire un gran mal di testa...",info:"Ordine: danneggia di 4 un nemico.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 4 un nemico.\n"},201523:{name:"Geralt: Yrden",flavor:"Si chinò sui resti mummificati di Adda, tracciando il segno Yrden sul lato interno del coperchio del sarcofago.",info:"Schieramento: riporta tutte le unità di una fila al loro valore di forza base.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: riporta tutte le unità di una fila al loro valore di forza base.\n"},201559:{name:"Volontari di Mahakam",flavor:"Alééé! Ohhhh! Oh oh oh! Preparate le chiappe, pagliacci! Stiamo arrivando! Vi prendiamo a calci fino a consumarci le suole!",info:"Schieramento, Prima fila: se è presente un Nano su questa fila, richiama su di essa una copia di questa unità dal tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: se è presente un Nano su questa fila, <keyword=summon>richiama</keyword> su di essa una copia di questa unità dal tuo mazzo.\n"},201574:{name:"Amico di Harald",flavor:"Quello più alto è Wilmar. Alla sua destra c'è Wilfred. E il ragazzo che balbetta è Wilhelm.",info:"Ultimo desiderio: danneggia di 2 un'unità nemica casuale.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: danneggia di 2 un'unità nemica casuale.\n"},201576:{name:"Mucca",flavor:"A Beauclair, tutto è migliore: il vino è più dolce, le vacche sono più grasse e le donne sono più belle.",info:"Condannata.\n",infoRaw:"<keyword=doomed>Condannata</keyword>.\n"},201578:{name:"An Craite: predatore",flavor:"Ho capito bene? Vuoi salpare per le Skellige? Quei barbari ti faranno a pezzi!",info:"Schieramento, Prima fila: distribuisce casualmente 4 danni tra tutte le altre unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distribuisce casualmente 4 danni tra tutte le altre unità.\n"},201579:{name:"Fenice",flavor:"Cos'è nato prima? L'uovo o la gallina? Al cospetto di un paradosso vivente come la fenice, è decisamente un dilemma banale.",info:"All'inizio del round, se la Fenice è nel tuo cimitero, viene bandita, poi evoca una Fenice giovane e la richiama su una fila alleata casuale.\n",infoRaw:"All'inizio del round, se la Fenice è nel tuo cimitero, viene <keyword=banish>bandita</keyword>, poi <keyword=spawn>evoca</keyword> una Fenice giovane e la <keyword=summon>richiama</keyword> su una fila alleata casuale.\n"},201580:{name:"Usurpatore",flavor:"Perché dovrebbe essere il diritto di nascita a stabilire chi ci governa?",info:"All'inizio della partita, disattiva il comandante nemico per l'intera durata della battaglia.\n",infoRaw:"All'inizio della partita, disattiva il comandante nemico per l'intera durata della battaglia.\n"},201581:{name:"Pietra Stribog",flavor:"I mastri runici di Ofier possono combinarle in parole runiche di incredibile potere.",info:"Crea e gioca una carta Bronzo di Skellige.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo di Skellige.\n"},201582:{name:"Pietra Zoria",flavor:"Percepisco un certo gelo provenire da questa pietra runica. Ho in qualche modo ferito i suoi sentimenti?",info:"Crea e gioca una carta Bronzo dei Regni Settentrionali.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo dei Regni Settentrionali.\n"},201583:{name:"Pietra Dazhbog",flavor:"Attenzione: è ancora calda.",info:"Crea e gioca una carta Bronzo di Nilfgaard.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo di Nilfgaard.\n"},201584:{name:"Pietra Devana",flavor:"Le mie lame sono così affilate che possono tagliare la carta!",info:"Crea e gioca una carta Bronzo dei Mostri.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo dei Mostri.\n"},201585:{name:"Pietra Morana",flavor:"Più la guardo, più mi sento svenire...",info:"Crea e gioca una carta Bronzo degli Scoia'tael.\n",infoRaw:"<keyword=create>Crea</keyword> e gioca una carta Bronzo degli Scoia'tael.\n"},201587:{name:"Collina dei Sussurri",flavor:"Risorgerà. Da qualche altra parte, fuori dalla nostra portata. Il sangue scorrerà di nuovo.",info:"Distrugge un'unità alleata, poi evoca e mette in gioco una copia base di tale unità.\n",infoRaw:"Distrugge un'unità alleata, poi <keyword=spawn>evoca</keyword> e mette in gioco una copia base di tale unità.\n"},201589:{name:"Filavandrel aén Fidháil",flavor:"Anche se siamo pochi e dispersi, i nostri cuori ardono più che mai.",info:"Ordine: potenzia di 1 tutte le unità nella tua mano.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 1 tutte le unità nella tua mano.\n"},201595:{name:"Principessa Adda",flavor:"La maledizione fu annullata, ma il suo appetito per la carne cruda non svanì.",info:"Ordine: danneggia di 8 un'unità.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 8 un'unità.\n"},201597:{name:"Eist Tuirseach",flavor:"Eist arrivò a Cintra per aiutare Crach an Craite a ottenere la mano della Principessa Pavetta, ma finì per conquistare il cuore della madre di lei.",info:"Ordine: metti in gioco un Guerriero dal tuo cimitero.\n",infoRaw:"<keyword=order>Ordine</keyword>: metti in gioco un Guerriero dal tuo cimitero.\n"},201598:{name:"Talismano d'osso",flavor:"A volte l'oggetto più semplice possiede i poteri più formidabili.",info:"Potenzia di 1 tutti gli alleati.\n",infoRaw:"Potenzia di 1 tutti gli alleati.\n"},201599:{name:"Gatto mannaro",flavor:"Odia quando gli fai i grattini sulla pancia.",info:"Prosperità.\nSchieramento: danneggia di 1 tutti i nemici della fila opposta.\nUltimo desiderio: ripete l'abilità Schieramento.\n",infoRaw:"<keyword=thrive>Prosperità</keyword>.\n<keyword=deploy>Schieramento</keyword>: danneggia di 1 tutti i nemici della fila opposta.\n<keyword=deathwish>Ultimo desiderio</keyword>: ripete l'abilità <keyword=deploy>Schieramento</keyword>.\n"},201600:{name:"Lupo mannaro",flavor:"Alcuni dicono che la licantropia sia contagiosa: se un lupo mannaro ti morde, diventi come lui. I witcher sanno bene che è una mera superstizione: solo un potente maleficio può causare questa condizione.",info:"Immune.\n",infoRaw:"<keyword=immune>Immune</keyword>.\n"},201601:{name:"Invocazione di Yennefer",flavor:"Meglio non essere d'intralcio a Yennefer, soprattutto quando è di fretta.",info:"Scegli un'unità o un manufatto in campo e mettilo in cima al mazzo del suo possessore.\n",infoRaw:"Scegli un'unità o un manufatto in campo e mettilo in cima al mazzo del suo possessore.\n"},201603:{name:"Letho: regicida",flavor:"Perché questo monaco indossa guanti borchiati...?",info:"Schieramento: si trasforma in una copia di un'altra unità in campo senza cambiare di forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si trasforma in una copia di un'altra unità in campo senza cambiare di forza.\n"},201606:{name:"Codabiforca",flavor:"Quanto ai codabiforca, ecco come attirarli: pianta un palo nel terreno, lega ad esso una capra, indi poscia nasconditi nei paraggi.",info:"Schieramento: danneggia di 1 tutte le altre unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 tutte le altre unità.\n"},201609:{name:"Cacciatore (schiavi)",flavor:"Il trucco è spezzare la loro volontà, ma non il loro corpo.",info:"Schieramento: se sono presenti 4 o più unità sulla fila opposta, sequestra il nemico meno forte di quella fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: se sono presenti 4 o più unità sulla fila opposta, <keyword=seize>sequestra</keyword> il nemico meno forte di quella fila.\n"},201610:{name:"Fanteria (schiavi)",flavor:"Un uomo libero può scegliere, ma nel caso di uno schiavo sono altri a scegliere per lui.",info:"Schieramento: trasforma un'unità alleata in una Fanteria di schiavi.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: trasforma un'unità alleata in una Fanteria di schiavi.\n"},201611:{name:"Azzardo di Iorveth",flavor:"A Iorveth sarà anche rimasto un solo occhio, ma le sue visioni interiori sono impareggiabili.",info:"Se hai almeno 4 Trappole nel tuo mazzo iniziale, mettine in gioco 2 casuali.\n",infoRaw:"Se hai almeno 4 Trappole nel tuo mazzo iniziale, mettine in gioco 2 casuali.\n"},201612:{name:"Schiavista",flavor:"Perché uno sceglie di fare lo schiavista? Il motivo è semplice: vedere i propri ordini seguiti alla lettera.",info:"Schieramento: danneggia di 2 un nemico. Aumenta di 1 i danni per ogni unità bloccata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 2 un nemico. Aumenta di 1 i danni per ogni unità <keyword=lock>bloccata</keyword>.\n"},201613:{name:"Saesenthessis: fuoco",flavor:"Ho ereditato da mio padre la capacità di assumere altre forme... beh, una sola, nel mio caso.",info:"Ordine: danneggia di 1 un nemico. \nCariche: 5.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un nemico. \n<keyword=charge>Cariche</keyword>: 5.\n"},201615:{name:"Consiglio di Isengrim",flavor:"Davanti a noi c'è il Passo di Elskerdeg, e oltre di esso ci sono Zerrikania e Haklan. Ci aspetta un viaggio lungo e pericoloso. Se dobbiamo affrontarlo insieme, mettiamo da parte la diffidenza.",info:"Guarda un Nano, una Driade e un Elfo a caso presenti nel tuo mazzo e mettine in gioco uno.\n",infoRaw:"Guarda un Nano, una Driade e un Elfo a caso presenti nel tuo mazzo e mettine in gioco uno.\n"},201616:{name:"Dissimulatore",flavor:"Oh, Grande Sole che sei nei cieli, salvaci dai parassiti che infestano le nostre braghe.",info:"Ordine: scambia la forza di questa unità con quella di un nemico.\n",infoRaw:"<keyword=order>Ordine</keyword>: scambia la forza di questa unità con quella di un nemico.\n"},201617:{name:"Recluta",flavor:"E, senza sapere perché, si trova a pelare una dannata patata dopo l'altra...",info:"Schieramento: svela un'unità casuale in entrambi i mazzi. Se la tua è più forte, questa carta si potenzia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=reveal>svela</keyword> un'unità casuale in entrambi i mazzi. Se la tua è più forte, questa carta si potenzia di 2.\n"},201618:{name:"Seltkirk di Gulet",flavor:"Famoso per le numerose virtù, tra le quali spiccava l'indomito coraggio, Seltkirk non venne risparmiato dalla sorte che toccò a coloro che furono uccisi nella Battaglia dell'Aerdin Superiore.",info:"Ordine, Prima fila: duella con un'unità nemica.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=duel>duella</keyword> con un'unità nemica.\n"},201619:{name:"Unguento",flavor:"Sarà utile? Chissà... Male non può fare. Forse.",info:"Potenzia di 2 un Soldato Bronzo e riattiva la sua abilità Schieramento.\n",infoRaw:"Potenzia di 2 un Soldato Bronzo e riattiva la sua abilità <keyword=deploy>Schieramento</keyword>.\n"},201620:{name:"Vandergrift",flavor:"Il generale si immaginava che la guerra per il Lormark sarebbe finita presto e con perdite minime... e invece si ritrovò a combattere per l'eternità.",info:"Ordine, Prima fila: se controlli Spada di Vandergrift, danneggia di 2 un'unità, altrimenti la danneggia di 1.\nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: se controlli Spada di Vandergrift, danneggia di 2 un'unità, altrimenti la danneggia di 1.\n<keyword=cooldown>Recupero</keyword>: 1.\n"},201621:{name:"Kiyan",flavor:"Viviamo su una tranquilla isola di ignoranza in mezzo a un nero mare di infinito. Non era previsto che tentassimo di avventurarci oltre.",info:"Ordine: danneggia un'unità di un valore pari alle Cariche di Kiyan.\nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia un'unità di un valore pari alle <keyword=charge>Cariche</keyword> di Kiyan.\n<keyword=charge>Cariche</keyword>: 1.\n"},201622:{name:"Kaedwen: cavaliere",flavor:"Non tutti i soldati kaedweniani appoggiano la politica del re, ma nessuno oserebbe mai affermarlo a voce alta.",info:"Schieramento: si potenzia di 4 se viene messa in gioco dal mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 4 se viene messa in gioco dal mazzo.\n"},201623:{name:"Cinghiale gigante",flavor:"I più, quando vedono un enorme cinghiale tra i cespugli, se la fanno sotto e si arrampicano sull'albero più vicino. Un abitante delle Skellige, invece, si lecca i baffi.",info:"Schieramento, Prima fila: si potenzia di 1 per ogni unità danneggiata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: si potenzia di 1 per ogni unità danneggiata.\n"},201624:{name:"Kaedwen: revenant",flavor:"Condannato a vivere lo stesso giorno per l'eternità, un po' come gli impiegati della Banca Cianfanelli.",info:"Ordine: danneggia di 1 un'unità. Se essa viene distrutta, evoca una copia base di Kaedwen: revenant e la richiama su questa fila.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. Se essa viene distrutta, <keyword=spawn>evoca</keyword> una copia base di Kaedwen: revenant e la <keyword=summon>richiama</keyword> su questa fila.\n"},201625:{name:"Cavaliere maledetto",flavor:"Combatte una guerra senza fine di cui non ricorda l'inizio.",info:"Schieramento: trasforma un'unità alleata diversa dal Cavaliere maledetto in quest'ultimo senza cambiarne la forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: trasforma un'unità alleata diversa dal Cavaliere maledetto in quest'ultimo senza cambiarne la forza.\n"},201626:{name:"Ciri: esplosione",flavor:"Zireael possiede un immenso potere che non può controllare. È un pericolo per sé e per gli altri. Finché non avrà imparato a domarlo, deve restare isolata.",info:"All'inizio del turno, se questa unità è l'unica nel tuo cimitero, la richiama su una fila alleata casuale.\n",infoRaw:"All'inizio del turno, se questa unità è l'unica nel tuo cimitero, la <keyword=summon>richiama</keyword> su una fila alleata casuale.\n"},201627:{name:"Empørio: ferie",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Condannata.\nSe il tuo mazzo iniziale non contiene carte doppie, manda Empørio all'avventura.\n",infoRaw:"<keyword=doomed>Condannata</keyword>.\nSe il tuo mazzo iniziale non contiene carte doppie, manda Empørio all'avventura.\n"},201628:{name:"Magia proibita",flavor:"Proibita? Certo. Scomparsa? Tutt'altro.",info:"Danneggia di 1 un nemico per ogni Spettro alleato.\n",infoRaw:"Danneggia di 1 un nemico per ogni Spettro alleato.\n"},201630:{name:"Maga dannata",flavor:"La maledizione di Sabrina non ha risparmiato nessuno, neppure le sue colleghe.",info:"Schieramento: danneggia di 3 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 3 un nemico.\n"},201631:{name:"Mutaforma",flavor:"Gli umani odiano i mutaforma così tanto che condannarli a morte non basta. È per questo che li legano a un palo, li ricoprono con uno spesso strato di argilla e poi li danno alle fiamme.",info:"Schieramento: scegli un'unità nella tua mano, dopodiché potenzia la presente carta di un valore pari al numero di unità nella tua mano che condividono la categoria primaria dell'unità scelta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: scegli un'unità nella tua mano, dopodiché potenzia la presente carta di un valore pari al numero di unità nella tua mano che condividono la categoria primaria dell'unità scelta.\n"},201632:{name:"Sihil",flavor:"Cosa c'è scritto su questa spada? Una maledizione? No, un insulto.",info:"Fervore.\nOrdine: danneggia di 1 un nemico.\nRecupero: 2.\nOgni volta che Sihil distrugge un nemico, aumenta di 1 i danni dell'abilità Ordine di Sihil finché non viene rimosso dal campo di battaglia.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: danneggia di 1 un nemico.\n<keyword=cooldown>Recupero</keyword>: 2.\nOgni volta che Sihil distrugge un nemico, aumenta di 1 i danni dell'abilità <keyword=order>Ordine</keyword> di Sihil finché non viene rimosso dal campo di battaglia.\n"},201633:{name:"Flagello sanguinario",flavor:"Esistono armi bandite in tutti i Regni Settentrionali, poiché l'orrore delle ferite che infliggono va oltre ogni immaginazione.",info:"Fervore.\nOrdine: danneggia un nemico di un valore pari alle Cariche di Flagello sanguinario, poi perde tutte le Cariche.\nCariche: 1.\nOgni volta che un'unità alleata ottiene Cariche, questa unità ne ottiene la stessa quantità.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: danneggia un nemico di un valore pari alle <keyword=charge>Cariche</keyword> di Flagello sanguinario, poi perde tutte le <keyword=charge>Cariche</keyword>.\n<keyword=charge>Cariche</keyword>: 1.\nOgni volta che un'unità alleata ottiene <keyword=charge>Cariche</keyword>, questa unità ne ottiene la stessa quantità.\n"},201634:{name:"Frana di Bekker",flavor:"Un battito di farfalla e siamo tutti spacciati.",info:"Danneggia di 8 un'unità.\n",infoRaw:"Danneggia di 8 un'unità.\n"},201635:{name:"Caccia gloriosa",flavor:"Per le gonadi degli dei, witcher! Perché mi porti questa schifezza?!? Voglio la testa di quella bestia era solo un modo di dire!",info:"Distrugge l'unità con il valore di forza base più alto.\n",infoRaw:"Distrugge l'unità con il valore di forza base più alto.\n"},201636:{name:"Mezzelfo cacciatore",flavor:"Odiati dagli uomini, oltraggiati dagli elfi e scelti sempre per ultimi nei giochi campestri a scuola. Non sorprende che i mezzelfi covino un certo rancore...",info:"Schieramento: per ogni manufatto che controlli, danneggia di 1 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: per ogni manufatto che controlli, danneggia di 1 un nemico.\n"},201637:{name:"Sogno di drago",flavor:"In Zerrikania, il culto dei draghi permea ogni aspetto della vita quotidiana. Non stupisce, quindi, che anche i nomi delle armi siano in loro onore.",info:"Applica questo effetto a una fila nemica:\nDopo 3 turni, all'inizio del turno, crea un'esplosione che danneggia di 3 tutte le unità di questa fila.\n",infoRaw:"Applica questo effetto a una fila nemica:\nDopo 3 turni, all'inizio del turno, crea un'esplosione che danneggia di 3 tutte le unità di questa fila.\n"},201638:{name:"Elfa esploratrice",flavor:"Dicono che gli elfi non lascino tracce nella neve. Per come la vedo io, chi lo dice è solo uno zotico ignorante.",info:"Si potenzia di 2 ogni volta che scatta una Trappola alleata.\n",infoRaw:"Si potenzia di 2 ogni volta che scatta una Trappola alleata.\n"},201639:{name:"Guarnigione",flavor:"Toc toc. C'è nessuno in casa?",info:"Potenzia di 10 un'unità.\n",infoRaw:"Potenzia di 10 un'unità.\n"},201644:{name:"Yoana",flavor:"Nessuno penserebbe mai che io sia un'abile fabbro... Un'umana, e per giunta donna. Ma un fabbro nanico... Quella è un'altra storia...",info:"Ordine: cura di 1 un alleato.\nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che un'unità adiacente viene danneggiata.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=heal>cura</keyword> di 1 un alleato.\n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che un'unità adiacente viene danneggiata.\n"},201645:{name:"Trappola mortale",flavor:"Quando ti stende, non ti rialzi più.",info:"Imboscata: dopo 3 turni, alla fine del turno, danneggia di 2 tutte le unità della fila avversaria più popolata. \nFervore.\nOrdine: gira questa carta e danneggia di 1 tutte le unità di una fila.\n",infoRaw:"<keyword=ambush>Imboscata</keyword>: dopo 3 turni, alla fine del turno, danneggia di 2 tutte le unità della fila avversaria più popolata. \n<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: gira questa carta e danneggia di 1 tutte le unità di una fila.\n"},201646:{name:"Derran",flavor:"A causare una tale follia deve essere stato qualcosa di spaventoso...",info:"Schieramento, Retrovie: sposta fino a 3 carte dal tuo mazzo al tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: sposta fino a 3 carte dal tuo mazzo al tuo cimitero.\n"},201647:{name:"Raffica di pietre",flavor:"Se decide di farti la festa, un macigno t'arriva in testa.",info:"Danneggia di 4 un nemico e lo sposta sull'altra fila.\n",infoRaw:"Danneggia di 4 un nemico e lo sposta sull'altra fila.\n"},201648:{name:"Spada di Vandergrift",flavor:"Seltkirk spezzò la spada di Vandergrift durante un torneo ad Ard Carraigh. Vandergrift, furioso per l'inadeguatezza della sua arma, ne fece forgiare un'altra, questa volta con incastonate delle pietre runiche incantate.",info:"Ogni volta che metti in gioco un Soldato su questa fila, lo potenzia di 2.\n",infoRaw:"Ogni volta che metti in gioco un Soldato su questa fila, lo potenzia di 2.\n"},201650:{name:"Inferno di Sabrina",flavor:"Esalando l'ultimo respiro, Sabrina Glevissig lanciò un potente maleficio che colpì non solo i suoi carnefici, ma anche tutti gli astanti.",info:"Danneggia di 1 ogni unità non appartenente alla categoria Spettro. Se una di esse viene distrutta, ripeti l'abilità una volta.\n",infoRaw:"Danneggia di 1 ogni unità non appartenente alla categoria Spettro. Se una di esse viene distrutta, ripeti l'abilità una volta.\n"},201653:{name:"Corno di Mahakam",flavor:"Tanto tempo fa, Mahakam organizzò una gara di soffio nel corno. Quel giorno i nani fecero un'importante scoperta: suoni potenti e picchi innevati non vanno d'accordo.",info:"Imboscata: quando l'avversario passa, potenzia di 4 le unità adiacenti.\n",infoRaw:"<keyword=ambush>Imboscata</keyword>: quando l'avversario passa, potenzia di 4 le unità adiacenti.\n"},201656:{name:"Lancia meravigliosa",flavor:"Tieni alta quella lancia, mollaccione! Non deve passare nemmeno un cavallo!",info:"Fervore.\nOrdine: danneggia di 1 un'unità. \nRecupero: 1.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},201657:{name:"Parassita",flavor:"Un essere che... balza all'occhio.",info:"Danneggia di 6 un nemico o potenzia di 6 un alleato.\n",infoRaw:"Danneggia di 6 un nemico o potenzia di 6 un alleato.\n"},201659:{name:"Verricello",flavor:"È un verricello. Non c'è altro da aggiungere.",info:"Metti in gioco una Macchina da guerra Bronzo dal tuo cimitero.\n",infoRaw:"Metti in gioco una Macchina da guerra Bronzo dal tuo cimitero.\n"},201660:{name:"Ruehin",flavor:"Nessuno di coloro che sono entrati in quella foresta è sopravvissuto per raccontarlo...",info:"Ultimo desiderio: alla fine del turno, richiama questa unità dal tuo cimitero su una fila alleata casuale.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: alla fine del turno, <keyword=summon>richiama</keyword> questa unità dal tuo cimitero su una fila alleata casuale.\n"},201662:{name:"Cadaverina",flavor:"Non affido la mia vita al caso. La affido a una bella mano di cadaverina sulla mia lama.",info:"Fervore.\nOrdine: distrugge sé e tutte le unità con forza pari alle Cariche di Cadaverina.\nCariche: 0.\nAll'inizio di ogni turno e ogni volta che giochi una carta Strategia, aggiunge 1 alle proprie Cariche.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: distrugge sé e tutte le unità con forza pari alle <keyword=charge>Cariche</keyword> di Cadaverina.\n<keyword=charge>Cariche</keyword>: 0.\nAll'inizio di ogni turno e ogni volta che giochi una carta Strategia, aggiunge 1 alle proprie <keyword=charge>Cariche</keyword>.\n"},201664:{name:"Vrygheff",flavor:"Occhio per occhio funziona bene fintanto che il mondo intero non è rimasto cieco.",info:"Schieramento: attiva l'abilità Schieramento dei Soldati Bronzo adiacenti.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: attiva l'abilità <keyword=deploy>Schieramento</keyword> dei Soldati Bronzo adiacenti.\n"},201676:{name:"Pavko Gale",flavor:"Nessuno ha mai contrabbandato cibo per gli elfi delle Montagne Blu tanto quanto Gale. Portava loro grossi sacchi stracolmi di rape e, soprattutto, di succulenti porri.",info:"Ordine: danneggia di 1 un'unità. \nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},201696:{name:"Paulie Dahlberg",flavor:"Muovete quei deretani! È una maledetta trappola!",info:"Ordine: potenzia di 4 l'unità alla sua sinistra oppure fornisce Immunità all'unità alla sua destra.\n",infoRaw:"<keyword=order>Ordine</keyword>: potenzia di 4 l'unità alla sua sinistra oppure fornisce <keyword=immune>Immunità</keyword> all'unità alla sua destra.\n"},201697:{name:"Sangue nero",flavor:"I vampiri concordano sul fatto che usare questa pozione denota scarsa sportività.",info:"La prossima volta che un nemico viene potenziato, lo distrugge e si autodistrugge.\n",infoRaw:"La prossima volta che un nemico viene potenziato, lo distrugge e si autodistrugge.\n"},201698:{name:"Ozzrel",flavor:"Certi necrofagi hanno imparato ad amare la carne umana così tanto che dissotterrare cadaveri non gli basta più...",info:"Schieramento, Prima fila: consuma un'unità dal cimitero avversario.\nSchieramento, Retrovie: consuma un'unità dal tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=consume>consuma</keyword> un'unità dal cimitero avversario.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=consume>consuma</keyword> un'unità dal tuo cimitero.\n"},201700:{name:"Troll del ponte",flavor:"Questo ponte? I troll l'hanno costruito tanto tempo fa. Lo presidiavano e facevano pagare il pedaggio, ma non passava molta gente. Così hanno fatto i bagagli e sono andati via. Il ponte, però, l'hanno lasciato.",info:"Ultimo desiderio: potenzia di 4 l'alleato più forte.\n",infoRaw:"<keyword=deathwish>Ultimo desiderio</keyword>: potenzia di 4 l'alleato più forte.\n"},201701:{name:"Barbegazi",flavor:"Ciò che un attimo prima pareva rocce tondeggianti tra le stalagmiti ora lo fissava con occhi enormi e minacciosi...",info:"Ordine: consuma un'unità alleata.\nCariche: 2.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=consume>consuma</keyword> un'unità alleata.\n<keyword=charge>Cariche</keyword>: 2.\n"},201704:{name:"Esploratore",flavor:"Se gli esploratori non fanno ritorno, leviamo le tende. La gente del luogo dice che questi boschi pullulano di scoiattoli, ma non del tipo che sgranocchia le ghiande...",info:"Schieramento: rimuove un effetto su fila da questa fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: rimuove un effetto su fila da questa fila.\n"},201709:{name:"Uniformità",flavor:"Cosa c'è scritto su questa spada? Una maledizione? No, un insulto.",info:"Danneggia di 2 tutte le unità con forza dispari.\n",infoRaw:"Danneggia di 2 tutte le unità con forza dispari.\n"},201710:{name:"Intralcio",flavor:"Cosa c'è scritto su questa spada? Una maledizione? No, un insulto.",info:"Danneggia di 2 tutte le unità con forza pari.\n",infoRaw:"Danneggia di 2 tutte le unità con forza pari.\n"},201725:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Manda Empørio all'accademia per ragazzi di Ban Ard.\n",infoRaw:"Manda Empørio all'accademia per ragazzi di Ban Ard.\n"},201726:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: trasforma la carta all'estrema destra della mano di entrambi i giocatori in una carta Speciale casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: trasforma la carta all'estrema destra della mano di entrambi i giocatori in una carta Speciale casuale.\n"},201727:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: sposta un'unità casuale sulla fila opposta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: sposta un'unità casuale sulla fila opposta.\n"},201728:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: applica un effetto su fila casuale su tutte le file.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: applica un effetto su fila casuale su tutte le file.\n"},201729:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: danneggia casualmente tutte le altre unità in campo per un valore complessivo di 13.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia casualmente tutte le altre unità in campo per un valore complessivo di 13.\n"},201730:{name:"Empørio: mago",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: evoca e richiama un'unità casuale su una fila casuale in entrambe le metà di campo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=spawn>evoca</keyword> e <keyword=summon>richiama</keyword> un'unità casuale su una fila casuale in entrambe le metà di campo.\n"},201731:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Manda Empørio nelle foreste di Dol Blathanna.\n",infoRaw:"Manda Empørio nelle foreste di Dol Blathanna.\n"},201732:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: gioca una carta casuale dal tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: gioca una carta casuale dal tuo mazzo.\n"},201733:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: distribuisce casualmente 9 danni tra tutte le unità nemiche.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distribuisce casualmente 9 danni tra tutte le unità nemiche.\n"},201734:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: potenzia di una quantità compresa tra 1 e 2 tutte le unità nella tua mano.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di una quantità compresa tra 1 e 2 tutte le unità nella tua mano.\n"},201735:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: distrugge un nemico casuale.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un nemico casuale.\n"},201736:{name:"Empørio: cacciatore",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: applica un effetto su fila casuale su una fila nemica.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: applica un effetto su fila casuale su una fila nemica.\n"},201737:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Manda Empørio all'Accademia Militare della corte imperiale.\n",infoRaw:"Manda Empørio all'Accademia Militare della corte imperiale.\n"},201738:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: danneggia di 4 un nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 4 un nemico.\n"},201739:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: blocca un'unità.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: <keyword=lock>blocca</keyword> un'unità.\n"},201740:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: ottiene Resistenza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: ottiene <keyword=resilient>Resistenza</keyword>.\n"},201741:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: distrugge un manufatto nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico.\n"},201742:{name:"Empørio: cavaliere",flavor:"Gli altri troll lo considerano un po' strano. Dopotutto, quale troll sano di mente preferirebbe dei foglietti di carta colorata alle pepite?",info:"Schieramento: potenzia di 4 un alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 4 un alleato.\n"},201743:{name:"Arachas regina",flavor:"La sua prole ne ha ereditato la mirabile bellezza.",info:"Ogni volta che un'unità viene distrutta durante il tuo turno, evoca un Arachas fuco e lo richiama su una fila alleata casuale.\n",infoRaw:"Ogni volta che un'unità viene distrutta durante il tuo turno, <keyword=spawn>evoca</keyword> un Arachas fuco e lo <keyword=summon>richiama</keyword> su una fila alleata casuale.\n"},201744:{name:"Spore",flavor:"Portate dal vento per tutto il Continente... ovunque cadano, seminano follia e rovina.",info:"Riporta un'unità alla sua forza base.\n",infoRaw:"Riporta un'unità alla sua forza base.\n"},201748:{name:"Orgoglio di Foltest",flavor:"Come piace dire a Re Foltest, non sono le dimensioni che contano, ma è saper portare a termine il lavoro.",info:"Ordine, Prima fila: danneggia di 1 un nemico e tutti i nemici che hanno la sua stessa forza. \nCariche: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 1 un nemico e tutti i nemici che hanno la sua stessa forza. \n<keyword=charge>Cariche</keyword>: 1.\n"},201749:{name:"Schiuma dorata",flavor:"Lo senti? È il profumo della felicità.",info:"Potenzia di 2 tutte le unità di una fila.\n",infoRaw:"Potenzia di 2 tutte le unità di una fila.\n"},201753:{name:"Milizia contadina",flavor:"Siamo la milizia. Manteniamo la pace.",info:"Schieramento: potenzia di 2 un alleato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un alleato.\n"},201772:{name:"Geralt: professionale",flavor:"Accettai un lavoro e lo portai a termine. In quanto alla ricompensa, optai per la Legge della Sorpresa.",info:"Schieramento, Prima fila: se la forza di un nemico è multipla di 3, lo distrugge, altrimenti lo danneggia di 3.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: se la forza di un nemico è multipla di 3, lo distrugge, altrimenti lo danneggia di 3.\n"},201773:{name:"Triss: telecinesi",flavor:"Normali legacci non basterebbero, e neppure un bavaglio la renderebbe meno pericolosa. Il dimeritium è l'unica soluzione.",info:"Schieramento, Retrovie: crea e gioca una carta Speciale Bronzo proveniente dal mazzo attuale di uno dei due giocatori.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=create>crea</keyword> e gioca una carta Speciale Bronzo proveniente dal mazzo attuale di uno dei due giocatori.\n"},201774:{name:"Dandelion: vanità",flavor:"Dandelion mi ha raccontato delle tue gesta. Di come ti spronava con le sue melodie. Di quella volta in cui ammansì il kayran suonando il liuto...",info:"Schieramento: distrugge una Bestia nemica.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge una Bestia nemica.\n"},201776:{name:"Dandelion: poeta",flavor:"La penna ferisce più della spada.",info:"Schieramento: pesca una carta, poi gioca una carta.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: pesca una carta, poi gioca una carta.\n"},201777:{name:"Roche: spietato",flavor:"Non è da noi avere paura. Tuttavia, con questo d'hoine... Vernon Roche... è meglio non scherzarci troppo.",info:"Ordine: sequestra un nemico con forza pari o inferiore alle Cariche di Roche: spietato, poi perde tutte le Cariche.\nCariche: 0. \nAll'inizio di ogni turno, aggiunge 1 alle proprie Cariche.\n",infoRaw:"<keyword=order>Ordine</keyword>: <keyword=seize>sequestra</keyword> un nemico con forza pari o inferiore alle <keyword=charge>Cariche</keyword> di Roche: spietato, poi perde tutte le <keyword=charge>Cariche</keyword>.\n<keyword=charge>Cariche</keyword>: 0. \nAll'inizio di ogni turno, aggiunge 1 alle proprie <keyword=charge>Cariche</keyword>.\n"},201779:{name:"Richiamo della foresta",flavor:"Tengo a Brokilon più della mia stessa vita. È una madre che si preoccupa dei propri figli. La difenderò a qualsiasi costo.",info:"Rimescola un'unità alleata nel tuo mazzo, poi metti in gioco da esso un'unità diversa che ha la stessa categoria primaria e potenziala di 3.\n",infoRaw:"Rimescola un'unità alleata nel tuo mazzo, poi metti in gioco da esso un'unità diversa che ha la stessa categoria primaria e potenziala di 3.\n"},201780:{name:"Yennefer: divinazione",flavor:"Sì, ho visto il futuro. E tu non ci sei.",info:"Schieramento, Retrovie: svela un'unità casuale del tuo mazzo, poi potenzia un alleato di una quantità pari alla forza dell'unità svelata.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=reveal>svela</keyword> un'unità casuale del tuo mazzo, poi potenzia un alleato di una quantità pari alla forza dell'unità svelata.\n"},201781:{name:"Imlerith",flavor:"Le sorelle avevano previsto il tuo arrivo scrutando la superficie dell'acqua.",info:"Ordine: se controlli l'unità più forte in campo, danneggia di 2 un'unità. \nRecupero: 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: se controlli l'unità più forte in campo, danneggia di 2 un'unità. \n<keyword=cooldown>Recupero</keyword>: 1.\n"},201817:{name:"Ihuarraquax",flavor:"Inconcepibile... impossibile, pensò Ciri tornando in sé. Gli unicorni non esistono, non più, non in questo mondo. Gli unicorni sono estinti.",info:"Schieramento, Prima fila: entrambi i giocatori richiamano dal mazzo sulla fila del combattimento corpo a corpo l'unità con il costo di reclutamento più alto.\nSchieramento, Retrovie: entrambi i giocatori richiamano dal mazzo sulla fila del combattimento a distanza l'unità con il costo di reclutamento più basso.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: entrambi i giocatori <keyword=summon>richiamano</keyword> dal mazzo sulla fila del combattimento corpo a corpo l'unità con il costo di reclutamento più alto.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: entrambi i giocatori <keyword=summon>richiamano</keyword> dal mazzo sulla fila del combattimento a distanza l'unità con il costo di reclutamento più basso.\n"},202044:{name:"Gerwin lo Zoppo",flavor:"Tieni sempre il guinzaglio corto e non avere paura di usare la frusta!",info:"Schieramento, Prima fila: danneggia di 3 un nemico e tutte le sue copie.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 3 un nemico e tutte le sue copie.\n"},202054:{name:"Zuppa d'elfi",flavor:"Condire ogni porzione con formaggio grattugiato. Aggiungere bulbi oculari per insaporire il tutto. Servire calda.",info:"Distrugge 2 unità alleate dello stesso colore, poi crea e mette in gioco un'unità di quel colore.\n",infoRaw:"Distrugge 2 unità alleate dello stesso colore, poi <keyword=create>crea</keyword> e mette in gioco un'unità di quel colore.\n"},202064:{name:"Ivo di Belhaven",flavor:"Non è la ragione che mi manca, ma l'emozione.",info:"Ordine, Prima fila: danneggia di 2 un nemico.\nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che metti in gioco un Witcher.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=melee>Prima fila</keyword>: danneggia di 2 un nemico.\n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che metti in gioco un Witcher.\n"},202098:{name:"Eyck di Denesle",flavor:"Il libro sacro sostiene che il mondo è un giardino, donato agli uomini dagli dei. E noi uomini abbiamo trascurato questo giardino!",info:"Schieramento: ottiene Fervore se in mano hai un Drago.\nOrdine: distrugge un'unità con almeno 8 di forza.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: ottiene <keyword=zeal>Fervore</keyword> se in mano hai un Drago.\n<keyword=order>Ordine</keyword>: distrugge un'unità con almeno 8 di forza.\n"},202099:{name:"Gascon",flavor:"I Randagi di Spalla... Sei tu il loro capo? Colui che chiamano il Duca dei Cani?",info:"Schieramento, Prima fila: si potenzia di una quantità casuale compresa tra 0 e 11.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: si potenzia di una quantità casuale compresa tra 0 e 11.\n"},202100:{name:"Principe Villem",flavor:"Il ragazzo non è adatto a portare la corona. Non ha abbastanza arguzia né coraggio. Lo sappiamo tutti... tranne lui.",info:"Spia.\nSchieramento: gioca una carta Oro casuale dal tuo mazzo.\n",infoRaw:"<keyword=spying>Spia</keyword>.\n<keyword=deploy>Schieramento</keyword>: gioca una carta Oro casuale dal tuo mazzo.\n"},202101:{name:"Conte Caldwell",flavor:"Vostra Maestà... una regina che interroga il popolo... Non è appropriato...",info:"Alla fine di ogni turno, si sposta sulla fila con l'unità più forte in campo.\n",infoRaw:"Alla fine di ogni turno, si sposta sulla fila con l'unità più forte in campo.\n"},202102:{name:"Xavier Lemmens",flavor:"Il vostro ingegnere sarà anche conciato male, ma sa il fatto suo, questo è certo.",info:"Schieramento, Prima fila: bandisce fino a 3 carte del cimitero dell'avversario.\nSchieramento, Retrovie: bandisce fino a 3 carte del tuo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: <keyword=banish>bandisce</keyword> fino a 3 carte del cimitero dell'avversario.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: <keyword=banish>bandisce</keyword> fino a 3 carte del tuo cimitero.\n"},202103:{name:"Keltullis",flavor:"Quando si è stabilito qui, l'hanno combattuto per quasi un secolo. Alla fine, il drago si è stancato e loro hanno capito che non si sarebbe schiodato di lì, così... hanno trovato un accordo. Lui non li disturba, e loro gli portano delle offerte.",info:"Prima fila: alla fine del turno, distrugge l'unità meno forte nella metà di campo più popolata.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: alla fine del turno, distrugge l'unità meno forte nella metà di campo più popolata.\n"},202104:{name:"Terrorizzatore: sveglio",flavor:"Cosa ho fatto? urlò il mago, spaventato dalla sua creazione.",info:"Immune.\n",infoRaw:"<keyword=immune>Immune</keyword>.\n"},202105:{name:"Glustyworp",flavor:"A un tratto, l'acqua paludosa schizzò in tutte le direzioni. Due specie di enormi zampe di mantide costellate di aculei uscirono dal pantano. Quei mostruosi uncini verdi afferrarono un soldato, trascinando il poveretto sotto la superficie dell'acqua.",info:"Schieramento: distrugge tutte le unità che hanno 1 di forza. Per ogni unità distrutta, si potenzia di 2.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge tutte le unità che hanno 1 di forza. Per ogni unità distrutta, si potenzia di 2.\n"},202106:{name:"Gabor Zigrin",flavor:"Una volta, Brouver Hoog lo stava per cacciare da Mahakam per averlo beccato con un lama in soggiorno. È una lunga storia...",info:"Schieramento, Prima fila: ottiene Resistenza.\nSchieramento, Retrovie: ottiene Immunità.\nOgni volta che metti in gioco un Nano, si potenzia di 1.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: ottiene <keyword=resilient>Resistenza</keyword>.\n<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: ottiene <keyword=immune>Immunità</keyword>.\nOgni volta che metti in gioco un Nano, si potenzia di 1.\n"},202107:{name:"Barnabas Beckenbauer",flavor:"È arrivato qui con il suo carretto pieno di carabattole. È andato di casa in casa, lodando quella robaccia che vende. E che cosa ci ha rifilato? Bombe che ti esplodono in mano! Una formula per far crescere la barba che invece la fa bruciare!",info:"Schieramento: potenzia di 2 un Elfo, un Nano e una Driade alleati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: potenzia di 2 un Elfo, un Nano e una Driade alleati.\n"},202108:{name:"Isbel di Hagge",flavor:"Giurai che non avrei più usato la magia per nuocere agli altri.",info:"Ordine, Retrovie: guarda la prima carta di entrambi i mazzi. Tienine una e dai l'altra all'avversario.\n",infoRaw:"<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: guarda la prima carta di entrambi i mazzi. Tienine una e dai l'altra all'avversario.\n"},202109:{name:"Gudmund l'Insolente",flavor:"Ormai la costa è ripulita da tempo! Così, abbiamo risalito lo Yaruga, per l'oro e la gloria!",info:"Schieramento, Retrovie: sostituisci tutte le carte del tuo cimitero con quelle del tuo mazzo.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=ranged>Retrovie</keyword>: sostituisci tutte le carte del tuo cimitero con quelle del tuo mazzo.\n"},202110:{name:"Dagur Due Spade",flavor:"Gli scudi sono per le femminucce.",info:"Prima fila: si potenzia di 1 ogni volta che un nemico viene danneggiato.\n",infoRaw:"<keyword=melee>Prima fila</keyword>: si potenzia di 1 ogni volta che un nemico viene danneggiato.\n"},202111:{name:"Traheaern var Vdyffir",flavor:"I nilfgaardiani procedevano in direzione della regina senza il minimo accenno di nervosismo. L'uomo a capo della loro armata indossava ricche vesti. Emanava orgoglio... e profumo di muschio.",info:"Schieramento: guarda le prime 3 carte del mazzo avversario e spostane 1 nel suo cimitero.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: guarda le prime 3 carte del mazzo avversario e spostane 1 nel suo cimitero.\n"},202112:{name:"Reynard Odo",flavor:"Ho prestato servizio per dieci anni sotto vostro marito e per altri otto sotto di voi... e non ho mai combattuto per una causa migliore di questa.",info:"Ogni volta che viene messo in gioco un alleato dotato di Cariche, aggiunge 1 alle sue Cariche.\n",infoRaw:"Ogni volta che viene messo in gioco un alleato dotato di <keyword=charge>Cariche</keyword>, aggiunge 1 alle sue <keyword=charge>Cariche</keyword>.\n"},202113:{name:"Rayla la Nera",flavor:"Le eccezioni peggiorano solo le cose. Gli Scoia'tael devono morire e basta. Saranno gli dei a stabilire chi è innocente.",info:"Schieramento: danneggia un nemico di un valore pari al numero di Umani alleati.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia un nemico di un valore pari al numero di Umani alleati.\n"},202114:{name:"Fenice giovane",flavor:"Cos'è nato prima? L'uovo o la gallina? Al cospetto di un paradosso vivente come la fenice, è decisamente un dilemma banale.",info:"Condannata. Fervore.\nOrdine: si trasforma in una Fenice.\n",infoRaw:"<keyword=doomed>Condannata</keyword>. <keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>: si trasforma in una Fenice.\n"},202121:{name:"Randagi di Spalla",flavor:"Auuuuuuu, auuu, auuuuuuuu!",info:"Schieramento: sposta un'unità sull'altra fila.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: sposta un'unità sull'altra fila.\n"},202122:{name:"Trasportatore di bombe",flavor:"Conoscevo una coppia che si è separata subito dopo le nozze. Capita, quando c'è di mezzo una bomba piazzata sulla carrozza.",info:"Schieramento: distrugge un manufatto nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: distrugge un manufatto nemico.\n"},202148:{name:"Re Demavend III",flavor:"Il mio re apprezza... i piaceri della vita. A ogni modo, indulgere in tali diversivi non ha mai compromesso la sua capacità di governare il regno.",info:"Ordine: aggiungi 1 alle Cariche di un'unità. \nRecupero: 2.\n",infoRaw:"<keyword=order>Ordine</keyword>: aggiungi 1 alle <keyword=charge>Cariche</keyword> di un'unità. \n<keyword=cooldown>Recupero</keyword>: 2.\n"},202149:{name:"Angoulême",flavor:"Non importa se striscia, cammina o si arrampica. Mangio di tutto. Per quanto riguarda le cose che volano... una volta ho mangiato un aquilone perché era impiastricciato di farina.",info:"Ordine: metti in gioco un manufatto casuale dal mazzo avversario.\n",infoRaw:"<keyword=order>Ordine</keyword>: metti in gioco un manufatto casuale dal mazzo avversario.\n"},202150:{name:"Vysogota di Corvo",flavor:"È da quasi mezzo secolo che non pratico. Gli anni non sono stati benevoli con la mia memoria... e neppure con le mie mani.",info:"Fervore.\nOrdine, Retrovie: potenzia di 1 un'unità. \nCariche: 1.\nAggiunge 1 alle proprie Cariche ogni volta che uno dei due giocatori gioca una carta.\n",infoRaw:"<keyword=zeal>Fervore</keyword>.\n<keyword=order>Ordine</keyword>, <keyword=ranged>Retrovie</keyword>: potenzia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nAggiunge 1 alle proprie <keyword=charge>Cariche</keyword> ogni volta che uno dei due giocatori gioca una carta.\n"},202151:{name:"Falibor",flavor:"Da lontano sembra un orso. Da vicino... beh, un orso più grosso.",info:"Schieramento, Prima fila: distrugge un'unità nemica con Ordine.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>, <keyword=melee>Prima fila</keyword>: distrugge un'unità nemica con <keyword=order>Ordine</keyword>.\n"},202152:{name:"Anna Strenger",flavor:"Anna odiava il marito al punto che era pronta a fare qualsiasi cosa pur di sfuggirgli, insieme alla bambina che portava in grembo.",info:"Se alla fine del turno questa unità è potenziata, potenzia di 1 le unità adiacenti.\n",infoRaw:"Se alla fine del turno questa unità è potenziata, potenzia di 1 le unità adiacenti.\n"},202153:{name:"Lyria: cavalleria",flavor:"Un cavaliere non vale meno del suo destriero. E i cavalli lyriani sono tra i migliori del continente.",info:"Si potenzia di 1 ogni volta che metti in gioco un'unità dotata di Ordine.\n",infoRaw:"Si potenzia di 1 ogni volta che metti in gioco un'unità dotata di <keyword=order>Ordine</keyword>.\n"},202154:{name:"Rivia: picchiere",flavor:"Scendi da cavallo e affrontami!",info:"Schieramento: danneggia di 1 un nemico. Se viene distrutto, danneggia di 2 un altro nemico.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: danneggia di 1 un nemico. Se viene distrutto, danneggia di 2 un altro nemico.\n"},202155:{name:"Lyria: arbalesta",flavor:"Posso fare centro da cento passi di distanza! D'accordo, forse non al primo colpo...",info:"Ordine: danneggia di 1 un'unità. \nCariche: 1.\nOgni volta che metti in gioco una carta con Ordine, aggiunge 1 alle proprie Cariche.\n",infoRaw:"<keyword=order>Ordine</keyword>: danneggia di 1 un'unità. \n<keyword=charge>Cariche</keyword>: 1.\nOgni volta che metti in gioco una carta con <keyword=order>Ordine</keyword>, aggiunge 1 alle proprie <keyword=charge>Cariche</keyword>.\n"},202156:{name:"Lyria: lanzichenecco",flavor:"Meglio non ridere dei loro stupidi cappelli. Dammi retta.",info:"Ordine: se Lyria: lanzichenecco è potenziata, danneggia di 3 un'unità, altrimenti la danneggia di 1.\n",infoRaw:"<keyword=order>Ordine</keyword>: se Lyria: lanzichenecco è potenziata, danneggia di 3 un'unità, altrimenti la danneggia di 1.\n"},202161:{name:"Lyria: falciatore",flavor:"La nostra gente si sazia, i nostri nemici sanguinano.",info:"Schieramento: si potenzia di 1 per ogni alleato potenziato.\n",infoRaw:"<keyword=deploy>Schieramento</keyword>: si potenzia di 1 per ogni alleato potenziato.\n"}}}}]);